import { BreakpointObserver } from '@angular/cdk/layout';
import { ChangeDetectorRef, OnDestroy, Pipe, PipeTransform } from '@angular/core';
import { DdBreakPointsEnum } from '@digitaldelightsoy/design-library/src/navigation';
import { Subscription } from 'rxjs';

@Pipe({
    name: 'ddbreakpoint',
    pure: false,
})
export class BreakpointPipe implements PipeTransform, OnDestroy {
    constructor(private breakpointObserver: BreakpointObserver, private cdr: ChangeDetectorRef) {}

    private lastValue: boolean = false;
    private subscription?: Subscription;

    transform(breakpoint: keyof typeof DdBreakPointsEnum): boolean {
        this.subscription?.unsubscribe();
        this.subscription = this.breakpointObserver.observe(DdBreakPointsEnum[breakpoint]).subscribe((result) => {
            this.lastValue = result.matches;
            this.cdr.markForCheck();
        });

        return this.lastValue;
    }

    ngOnDestroy(): void {
        this.subscription?.unsubscribe();
        delete this.subscription;
    }
}

import { ChangeDetectorRef, OnDestroy, Pipe, PipeTransform } from '@angular/core';
import { Observable, Subscription, shareReplay } from 'rxjs';
import { DdCulture, DdLocaleService } from '../services/locale.service';

/**
 * CurrencyPipe renders an input string or decimal to decimal.
 * FI currencies are always shown with 2 decimals and other countries with 0-2 decimals.
 * Decimal separator comes from Intl.NumberFormat.
 * Usage:
 * {{price | ddcurrency}} renders the price with currency symbol,
 */
@Pipe({
    name: 'ddcurrency',
    pure: false,
})
export class CurrencyPipe implements PipeTransform, OnDestroy {
    constructor(private localeService: DdLocaleService, private cdr: ChangeDetectorRef) {
        this.localChanges$ = this.localeService.getLocalCulture$().pipe(shareReplay(1));
    }

    private readonly localChanges$: Observable<DdCulture>;

    private lastValue?: string;
    private subscription?: Subscription;

    transform(value: number | undefined | null): string | undefined {
        this.subscription?.unsubscribe();

        if (value === undefined || value === null) {
            return '';
        }

        // Create subscription for language changes that will format the new value and then call markForCheck to trigger change detection.
        this.subscription = this.localChanges$.subscribe((locale) => {
            // Minimum decimal amount should be always 2 if there are decimals to show, so if modulo 1 is 0 then the number has no decimals.
            const minimumFractionDigits: number = value % 1 === 0 ? 0 : 2;

            let currencySymbol = '';

            if (locale.includes('fi')) {
                // add EUR with space as the currency symbol for FI
                // Instead of space we're using a Non-breaking space to make prevent text wrapping from splitting value and currency
                currencySymbol = `${String.fromCharCode(160)}â‚¬`;
            } else if (locale.includes('sv')) {
                // Sweden has different symbol
                currencySymbol = ':-';
            } else {
                // Show rounded price symbol ',-' only if price does not have decimals.
                if (!minimumFractionDigits) {
                    currencySymbol = ',-';
                }
            }

            // Create format options.
            const options: Intl.NumberFormatOptions = {
                style: 'decimal',
                minimumFractionDigits,
                maximumFractionDigits: 2,
            };

            const currency = new Intl.NumberFormat(locale, options).format(value);

            this.lastValue = currency + currencySymbol;
            this.cdr.markForCheck();
        });

        return this.lastValue;
    }

    ngOnDestroy(): void {
        this.subscription?.unsubscribe();
        delete this.subscription;
    }
}

import { ChangeDetectorRef, OnDestroy, Pipe, PipeTransform } from '@angular/core';
import { format } from 'date-fns';
import { Observable, Subscription, shareReplay } from 'rxjs';
import { DdLocaleService } from '../services/locale.service';

/**
 * DatePipe uses moment to render a date value from input Date of date string.
 */
@Pipe({
    name: 'dddate',
    pure: false,
})
export class DatePipe implements PipeTransform, OnDestroy {
    constructor(private localeService: DdLocaleService, private cdr: ChangeDetectorRef) {
        this.localChanges$ = this.localeService.getDateLocale$().pipe(shareReplay(1));
    }

    private readonly localChanges$: Observable<Locale>;

    private lastValue?: string;
    private subscription?: Subscription;

    transform(value: Date | string | number | null | undefined): string | undefined {
        this.subscription?.unsubscribe();

        if (!value) {
            return '';
        }

        // Create tmp property from the input for the format function.
        let dateValue: Date | number;
        if (typeof value === 'string') {
            const timestamp = Date.parse(value);

            // Try to see if the date is faulty.
            if (isNaN(timestamp)) {
                return '';
            }

            dateValue = timestamp;
        } else {
            dateValue = value;
        }

        // Create subscription for language changes that will format the new value and then call markForCheck to trigger change detection.
        this.subscription = this.localChanges$.subscribe((l) => {
            this.lastValue = format(dateValue, this.localeService.getDateFormat(l), { locale: l }) ?? '';
            this.cdr.markForCheck();
        });

        return this.lastValue;
    }

    ngOnDestroy(): void {
        this.subscription?.unsubscribe();
        delete this.subscription;
    }
}

