import { Pipe, PipeTransform } from '@angular/core';

/** FilterPipe exposes possibility to give function which filters the input array. */
@Pipe({
    name: 'ddfilter',
})
export class FilterPipe implements PipeTransform {
    transform<T>(items: T[] | undefined, filterFunc: (item: T) => boolean): T[] {
        if (!items?.length || !filterFunc) {
            return [];
        }
        return items.filter((item) => filterFunc(item));
    }
}

import { Pipe, PipeTransform } from '@angular/core';

/** FindPipe exposes possibility to give function which finds an element from the input array. */
@Pipe({
    name: 'ddfind',
})
export class FindPipe implements PipeTransform {
    transform<T>(items: T[] | undefined, findFunc: (item: T) => boolean): T | undefined {
        if (!items?.length || !findFunc) {
            return undefined;
        }
        return items.find((item) => findFunc(item));
    }
}

import { Pipe, PipeTransform } from '@angular/core';
import { DomSanitizer, SafeHtml, SafeResourceUrl, SafeScript, SafeStyle, SafeUrl } from '@angular/platform-browser';

/**
 * Sanitizes a string so it can be safely rendered to template.
 * stringToBeSanitized | safe: 'type'
 */
@Pipe({
    name: 'safe',
})
export class SanitizerPipe implements PipeTransform {
    constructor(protected domSanitizer: DomSanitizer) {}

    transform(value: string, type: 'html' | 'style' | 'script' | 'url' | 'resourceUrl'): SafeHtml | SafeStyle | SafeScript | SafeUrl | SafeResourceUrl | undefined {
        if (!value) {
            return;
        }

        switch (type) {
            case 'html':
                return this.domSanitizer.bypassSecurityTrustHtml(value);
            case 'style':
                return this.domSanitizer.bypassSecurityTrustStyle(value);
            case 'script':
                return this.domSanitizer.bypassSecurityTrustScript(value);
            case 'url':
                return this.domSanitizer.bypassSecurityTrustUrl(value);
            case 'resourceUrl':
                return this.domSanitizer.bypassSecurityTrustResourceUrl(value);
            default:
                throw new Error(`Invalid safe type specified: ${type}`);
        }
    }
}

import { Pipe, PipeTransform } from '@angular/core';
import { SelectItem } from '../select-models';

@Pipe({
    name: 'ddSelectFilter',
})
export class SelectFilterPipe implements PipeTransform {
    transform(items: SelectItem<any>[], filter: string): SelectItem<any>[] {
        if (!items || !filter) {
            return items;
        }
        // filter items array, items which match and return true will be
        // kept, false will be filtered out
        return items.filter((item: SelectItem<any>) => item.text.toString().toLowerCase().indexOf(filter.toLowerCase()) !== -1);
    }
}

import { ChangeDetectorRef, OnDestroy, Pipe, PipeTransform } from '@angular/core';
import { Observable, Subscription, shareReplay } from 'rxjs';
import { DdCulture, DdLocaleService } from '../services/locale.service';

/**
 * Short date format pipe (day and month).
 * Note: This can removed if moment starts to support the short date format.
 */
@Pipe({
    name: 'ddshortdate',
    pure: false,
})
export class ShortDatePipe implements PipeTransform, OnDestroy {
    constructor(private localeService: DdLocaleService, private cdr: ChangeDetectorRef) {
        this.localChanges$ = this.localeService.getLocalCulture$().pipe(shareReplay(1));
    }

    private readonly localChanges$: Observable<DdCulture>;

    private lastValue?: string;
    private subscription?: Subscription;

    transform(value: Date | string | number | null | undefined): string | undefined {
        this.subscription?.unsubscribe();

        // Try to see if the date is faulty.
        if (!value) {
            return '';
        }

        // Create tmp property from the input for the format function.
        let dateValue: Date | number;
        if (typeof value === 'string') {
            const timestamp = Date.parse(value);

            // Try to see if the date is faulty.
            if (isNaN(timestamp)) {
                return '';
            }

            dateValue = timestamp;
        } else {
            dateValue = value;
        }

        this.subscription = this.localChanges$.subscribe((locale) => {
            const options: Intl.DateTimeFormatOptions = {
                day: '2-digit',
                month: '2-digit',
            };

            /**
             * '2-digit' format was returning 1 digit for Norway and Terje wanted to show short months.
             * So make an exception for Norway to display month in 'short' format.
             */
            if (locale === 'nb-no') {
                options.month = 'short';
            }

            // Format the date.
            const formattedShortDate = new Intl.DateTimeFormat(locale, options).format(dateValue);

            this.lastValue = formattedShortDate;
            this.cdr.markForCheck();
        });

        return this.lastValue;
    }

    ngOnDestroy(): void {
        this.subscription?.unsubscribe();
        delete this.subscription;
    }
}

import { Pipe, PipeTransform } from '@angular/core';

/** SortPipe exposes possibility to give function which sorts the input array. */
@Pipe({
    name: 'ddsort',
})
export class SortPipe implements PipeTransform {
    transform<T>(items: T[] | undefined, sortFunc: (a: T, b: T) => number): T[] {
        if (!items?.length || !sortFunc) {
            return [];
        }
        return items.sort((a: T, b: T) => sortFunc(a, b));
    }
}

