export interface KeyEvents {
    keyup: 'keyup';
    keydown: 'keydown';
}

/** Model for used keyboard keys. */
export interface KeyboardKeys {
    arrowdown: 'ArrowDown';
    arrowup: 'ArrowUp';
    enter: 'Enter';
    escape: 'Escape';
    tab: 'Tab';
    f1: 'F1';
}

/** Model for used keyboard key codes. */
export interface KeyboardKeyCodes {
    escape: 27;
}

/** Holds the different key events that could be used. */
export const keyEvents: KeyEvents = {
    keyup: 'keyup',
    keydown: 'keydown',
};

/**
 * Exports a constant object that holds specific keyboard keys used in the project.
 * https://developer.mozilla.org/en-US/docs/Web/API/KeyboardEvent/key/Key_Values
 */
export const keyboardKeys: KeyboardKeys = {
    arrowdown: 'ArrowDown',
    arrowup: 'ArrowUp',
    enter: 'Enter',
    escape: 'Escape',
    tab: 'Tab',
    f1: 'F1',
};

/**
 * The deprecated key codes.
 *
 * @deprecated KeyCodes are depricated (fallback to IE).
 */
export const keyboardKeyCodes: KeyboardKeyCodes = {
    escape: 27,
};

export type Unlisten = () => void;

export interface BindingsDictionary {
    [key: string]: Handler;
}

export interface ListenerOptions {
    priority: number;
    /**
     * Listener will stop here and not pass keyevent to lower priority listeners.
     * Default value true.
     */
    terminal?: boolean;

    emitInputs?: boolean;
}

export interface Listener {
    priority: number;
    bindings: BindingsDictionary;
    terminal: boolean;
    emitInputs: boolean;
}

export type Handler = (event: KeyboardEvent) => boolean | void;

import { Observable } from 'rxjs';

export interface ButtonGroupModel {
    /** Text for the button. */
    text: string;
    /** Adds aria label to the individual button. */
    ariaLabel: string;
    /** Adds dd-icon to button. */
    icon: string;
    /** Makes the button disabled, button does not output anything when it's disabled. */
    disabled?: boolean;
    /** Role attribute for individual button. */
    role?: string;
    /** Adds url support to button click. */
    url?: string | string[];
    /** Allows url to be opened in new tab. */
    target?: '_blank';
    /** Function to be called on button click. */
    buttonClick?: () => void;
    /** Hide arrow icon on button. */
    hideArrowIcon?: boolean;
    /** Loading indicator */
    loading$?: Observable<boolean>;
}

import { Component, EventEmitter, Inject, OnDestroy, OnInit, Output } from '@angular/core';
import { Observable, Subject } from 'rxjs';
import { take, takeUntil } from 'rxjs/operators';
import { OverlayAnimationState } from '../../models/animations';
import { OVERLAY_DATA } from '../../models/overlay';

export interface ConfirmSelectionOverlayModel {
    heading: string;
    bodyText?: string;
    cancelButtonText?: string;
    confirmButtonText?: string;
    useAnimation?: boolean;
    /** User can define action that is run when user clicks cancel button. */
    cancelSelection$?: () => Observable<boolean>;
    confirmSelection$: () => Observable<boolean>;
}

@Component({
    selector: 'dd-confirm-selection-overlay',
    templateUrl: './confirm-selection-overlay.component.html',
    styleUrls: ['./confirm-selection-overlay.component.scss'],
})
export class ConfirmSelectionOverlayComponent implements OnDestroy, OnInit {
    constructor(@Inject(OVERLAY_DATA) public data: ConfirmSelectionOverlayModel) {}

    @Output() closeOverlay: EventEmitter<boolean> = new EventEmitter();

    viewState?: OverlayAnimationState;

    private readonly _unsubscribeAllSubject: Subject<void> = new Subject();

    ngOnDestroy(): void {
        this._unsubscribeAllSubject.next();
        this._unsubscribeAllSubject.complete();
    }

    ngOnInit(): void {
        if (this.data?.useAnimation) {
            this.viewState = 'in-top';
        }
    }

    close(): void {
        if (this.data?.cancelSelection$) {
            this.data
                .cancelSelection$()
                .pipe(take(1), takeUntil(this._unsubscribeAllSubject))
                .subscribe((success: boolean) => {
                    if (success) {
                        this.closeOverlay.emit(false);
                    }
                });
        } else {
            this.closeOverlay.emit(false);
        }
    }

    confirm(): void {
        if (!this.data) {
            return;
        }

        this.data
            .confirmSelection$()
            .pipe(take(1), takeUntil(this._unsubscribeAllSubject))
            .subscribe((success: boolean) => {
                if (success) {
                    this.closeOverlay.emit(true);
                }
            });
    }
}

import { ChangeDetectionStrategy, Component, Input, OnChanges, OnInit } from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { DdLocaleService } from '@digitaldelightsoy/design-library/src/pipes';
import { Locale, addDays, addMonths, eachDayOfInterval, endOfDay, endOfMonth, format, getDate, getDay, getMonth, getYear, isAfter, isBefore, isSameDay, isSameMonth, isSameYear, isToday, setDay, startOfDay, startOfMonth, subDays } from 'date-fns';
import { Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';

/**
 * Represents a day in the datepicker.
 */
export interface Day {
    date: Date;
    day: number;
    month: number;
    year: number;
    inThisMonth: boolean;
    isToday: boolean;
    isSelected: boolean;
    isSelectable: boolean;
    /** If the Day is found from selectableDates it will be highlighted as green. */
    isHighlighted: boolean;
    hasNoFreeSlots: boolean;
}

/**
 * Configuration options for the datepicker.
 */
export interface DatepickerOptions {
    /** The earliest date that can be selected. */
    minDate?: Date | null;
    /** The latest date that can be selected. */
    maxDate?: Date | null;
    /** The day to display as the first day of the week. Sunday is 0. */
    firstCalendarDay?: number;
    /** Translations for the calendar legend. */
    legendTranslations?: DatepickerLegendTranslations;
    /** If true, all dates within the min and max dates are selectable. */
    allSelectable?: boolean;
}

/**
 * Translations for the calendar legend.
 */
export interface DatepickerLegendTranslations {
    selectedDate?: string;
    freeSlots?: string;
    currentDate?: string;
    allReserved?: string;
    noSlotsAvailable?: string;
}

/**
 * Default configuration options for the datepicker.
 */
export const defaultOptions: DatepickerOptions = {
    minDate: new Date(),
    maxDate: null,
    firstCalendarDay: 1,
    legendTranslations: undefined,
    allSelectable: false,
};

/**
 * Provides a datepicker component.
 */
@Component({
    selector: 'dd-datepicker',
    templateUrl: './datepicker.component.html',
    styleUrls: ['./datepicker.component.scss'],
    changeDetection: ChangeDetectionStrategy.Default,
    providers: [{ provide: NG_VALUE_ACCESSOR, useExisting: DatepickerComponent, multi: true }],
})
export class DatepickerComponent implements ControlValueAccessor, OnInit, OnChanges {
    constructor(localeService: DdLocaleService) {
        // Fetch the language locale data.
        localeService
            .getDateLocale$()
            .pipe(takeUntil(this._unsubscribeAllSubject))
            .subscribe((locale?: Locale) => {
                this.locale = locale;
            });

        this.days = [];
    }

    /** Configuration for the datepicker. */
    @Input() options: DatepickerOptions = { ...defaultOptions };

    /** Dates that can be selected. Also affected by @see DatepickerOptions min and max. */
    @Input() selectableDates: Date[] = [];

    /** Dates that can be selected and have free slots. */
    @Input() noFreeSlotsDates: Date[] = [];

    /**
     * The date (month) that the datepicker is currently showing.
     * If this value is initialized, that month is shown first.
     */
    @Input() date: Date = new Date();

    locale?: Locale;

    /** The currently selected date. */
    selected: Date = new Date();
    /** The days currently being shown. */
    days: Day[];

    /** The names of the weekdays for the header row. */
    dayNames?: string[];

    /** The current date to display in the legend. */
    currentDate?: Day;

    // eslint-disable-next-line rxjs/no-exposed-subjects
    private readonly _unsubscribeAllSubject: Subject<undefined> = new Subject();

    /**
     * Returns the title of the datepicker, which is the formatted date.
     * More info about formats: https://date-fns.org/v2.22.1/docs/format
     */
    get title(): string {
        return format(this.date, 'LLLL yyyy', { locale: this.locale });
    }

    get value(): Date {
        return this.selected;
    }

    set value(val: Date) {
        this.selected = val;
        this.onChangeCallback(this.selected);
    }

    ngOnInit(): void {
        this.initDays();
        this.dayNames = this.initDayNames();
    }

    ngOnChanges(): void {
        this.initDays();
    }

    selectDate(day: Day): void {
        this.date = day.date;
        this.value = day.date;

        for (const d of this.days) {
            this.updateDay(d);
        }
    }

    changeMonth(change: -1 | 1): void {
        this.date = addMonths(this.date, change);
        this.initDays();
    }

    private initDayNames(): string[] {
        const dayNames: string[] = [];
        const start = this.options.firstCalendarDay as number;
        for (let i = start; i <= 6 + start; i++) {
            const date = setDay(new Date(), i);
            dayNames.push(format(date, 'EEEEEE', { locale: this.locale }));
        }
        return dayNames;
    }

    /** Refreshes days array to match current date. */
    private initDays(): void {
        const [start, end] = [startOfMonth(this.date), endOfMonth(this.date)];

        this.days = eachDayOfInterval({ start, end }).map((d: Date) => this.generateDay(d));

        // Generate days from previous month
        let tmp = getDay(start) - (this.options.firstCalendarDay as number);
        const prevDays = tmp < 0 ? 7 - (this.options.firstCalendarDay as number) : tmp;
        for (let i = 1; i <= prevDays; i++) {
            const d: Date = subDays(start, i);
            this.days.unshift(this.generateDay(d, false));
        }

        // Generate days from next month
        tmp = 7 - getDay(end);
        const nextDays = tmp > 6 ? 0 : tmp;
        for (let i = 1; i <= nextDays; i++) {
            const d: Date = addDays(end, i);
            this.days.push(this.generateDay(d, false));
        }
    }

    private generateDay(date: Date, inThisMonth: boolean = true): Day {
        // Reset time, as we dont care about that.
        date.setHours(0, 0, 0, 0);

        const day = {
            date,
            day: getDate(date),
            month: getMonth(date),
            year: getYear(date),
            inThisMonth,
            isToday: isToday(date),
            isSelected: !!this.selected && this.isSameDate(date, this.selected),
            isSelectable: this.isValidDate(date, this.options.allSelectable),
            isHighlighted: this.isValidDate(date),
            hasNoFreeSlots: this.hasNoFreeSlots(date),
        };

        if (day.isToday) {
            this.currentDate = day;
        }

        return day;
    }

    private updateDay(day: Day): void {
        day.isSelected = !!this.selected && this.isSameDate(day.date, this.selected);
    }

    private isValidDate(date: Date, allSelectable?: boolean): boolean {
        if (this.options.minDate && isBefore(date, startOfDay(this.options.minDate))) {
            return false;
        }

        if (this.options.maxDate && isAfter(date, endOfDay(this.options.maxDate))) {
            return false;
        }

        // Any dates outside selected month should not be selectable.
        if (!isSameMonth(date, this.date)) {
            return false;
        }

        // only selectableDates are valid unless allSelectable option is enabled.
        return !!allSelectable || this.selectableDates.some((x) => this.isSameDate(date, x));
    }

    /** Check if two dates are equal. Ignores time. */
    private isSameDate(a: Date, b: Date): boolean {
        if (!isSameDay(a, b)) {
            return false;
        }
        if (!isSameMonth(a, b)) {
            return false;
        }
        if (!isSameYear(a, b)) {
            return false;
        }

        return true;
    }

    private hasNoFreeSlots(date: Date): boolean {
        return this.noFreeSlotsDates.some((x) => this.isSameDate(date, x));
    }

    writeValue(val: Date): void {
        if (!val) {
            return;
        }
        this.selected = val;
        this.initDays();
