    /** The currently selected date. */
    selected: Date = new Date();
    /** The days currently being shown. */
    days: Day[];

    /** The names of the weekdays for the header row. */
    dayNames?: string[];

    /** The current date to display in the legend. */
    currentDate?: Day;

