    private readonly _unsubscribeAllSubject: Subject<undefined> = new Subject();

    /**
     * Returns the title of the datepicker, which is the formatted date.
     * More info about formats: https://date-fns.org/v2.22.1/docs/format
     */
    get title(): string {
        return format(this.date, 'LLLL yyyy', { locale: this.locale });
    }

    get value(): Date {
        return this.selected;
    }

    set value(val: Date) {
        this.selected = val;
        this.onChangeCallback(this.selected);
    }

    ngOnInit(): void {
        this.initDays();
        this.dayNames = this.initDayNames();
    }

    ngOnChanges(): void {
        this.initDays();
    }

    selectDate(day: Day): void {
        this.date = day.date;
        this.value = day.date;

        for (const d of this.days) {
            this.updateDay(d);
        }
    }

    changeMonth(change: -1 | 1): void {
        this.date = addMonths(this.date, change);
        this.initDays();
    }

    private initDayNames(): string[] {
        const dayNames: string[] = [];
        const start = this.options.firstCalendarDay as number;
        for (let i = start; i <= 6 + start; i++) {
            const date = setDay(new Date(), i);
            dayNames.push(format(date, 'EEEEEE', { locale: this.locale }));
        }
        return dayNames;
    }

    /** Refreshes days array to match current date. */
    private initDays(): void {
        const [start, end] = [startOfMonth(this.date), endOfMonth(this.date)];

        this.days = eachDayOfInterval({ start, end }).map((d: Date) => this.generateDay(d));

        // Generate days from previous month
        let tmp = getDay(start) - (this.options.firstCalendarDay as number);
        const prevDays = tmp < 0 ? 7 - (this.options.firstCalendarDay as number) : tmp;
        for (let i = 1; i <= prevDays; i++) {
            const d: Date = subDays(start, i);
            this.days.unshift(this.generateDay(d, false));
        }

        // Generate days from next month
        tmp = 7 - getDay(end);
        const nextDays = tmp > 6 ? 0 : tmp;
        for (let i = 1; i <= nextDays; i++) {
            const d: Date = addDays(end, i);
            this.days.push(this.generateDay(d, false));
        }
    }

    private generateDay(date: Date, inThisMonth: boolean = true): Day {
        // Reset time, as we dont care about that.
        date.setHours(0, 0, 0, 0);

        const day = {
            date,
            day: getDate(date),
            month: getMonth(date),
            year: getYear(date),
            inThisMonth,
            isToday: isToday(date),
            isSelected: !!this.selected && this.isSameDate(date, this.selected),
            isSelectable: this.isValidDate(date, this.options.allSelectable),
            isHighlighted: this.isValidDate(date),
            hasNoFreeSlots: this.hasNoFreeSlots(date),
        };

        if (day.isToday) {
            this.currentDate = day;
        }

        return day;
    }

    private updateDay(day: Day): void {
        day.isSelected = !!this.selected && this.isSameDate(day.date, this.selected);
    }

    private isValidDate(date: Date, allSelectable?: boolean): boolean {
        if (this.options.minDate && isBefore(date, startOfDay(this.options.minDate))) {
            return false;
        }

        if (this.options.maxDate && isAfter(date, endOfDay(this.options.maxDate))) {
            return false;
        }

        // Any dates outside selected month should not be selectable.
        if (!isSameMonth(date, this.date)) {
            return false;
        }

        // only selectableDates are valid unless allSelectable option is enabled.
        return !!allSelectable || this.selectableDates.some((x) => this.isSameDate(date, x));
    }

    /** Check if two dates are equal. Ignores time. */
    private isSameDate(a: Date, b: Date): boolean {
        if (!isSameDay(a, b)) {
            return false;
        }
        if (!isSameMonth(a, b)) {
            return false;
        }
        if (!isSameYear(a, b)) {
            return false;
        }

        return true;
    }

    private hasNoFreeSlots(date: Date): boolean {
        return this.noFreeSlotsDates.some((x) => this.isSameDate(date, x));
    }

    writeValue(val: Date): void {
        if (!val) {
            return;
        }
        this.selected = val;
        this.initDays();
    }

    registerOnChange(fn: any): void {
        this.onChangeCallback = fn;
    }

    registerOnTouched(fn: any): void {
        this.onTouchedCallback = fn;
    }

    private onTouchedCallback: () => void = () => {};
    private onChangeCallback: (_: any) => void = () => {};
}

