import { Observable } from 'rxjs';

/**
 * Interface for navigation items, defines all necessary properties for navigation components.
 */
export interface NavigationItem {
    id: string;

    /**
     * Title shown in the naviation item.
     */
    title: string;

    /**
     * Navigation item type.
     * 'title' is a title for a group of 'collapsable' or 'item' typed navigation items.
     * 'collapsable' is a navigation item that can be expanded to show children.
     * 'item' is a normal navigation item.
     */
    type: 'item' | 'collapsable' | 'title';

    /**
     * Icon for the navigation item.
     */
    icon?: string;

    /**
     * RouterLink url. Should only be undefined if type is 'collapsable'
     */
    url?: string;

    /**
     * Children for the navigation item. Only shown with 'collapsable'.
     */
    children?: NavigationItem[];

    /**
     * If the user can navigate to the url.
     * The element is hidden if the user press the item.
     * NOTE: this itself does not block the navigation to the item, only hides it.
     */
    canNavigate$?: Observable<boolean>;

    /**
     * If the item is marked as favorite then icon for marking it as favorite is not shown.
     *
     * If item is marked as favorite, then it cannot be added from normal navigation.
     * Setting this to true to all items will disable the feature.
     */
    hideFavorite$?: Observable<boolean>;

    /**
     * Rendered with the tab to display the number of any notifications.
     */
    notifications?: number;
}

