import { ChangeDetectionStrategy, ChangeDetectorRef, Component, EventEmitter, Input, OnDestroy, OnInit, Output, ViewChild } from '@angular/core';
import { NavigationEnd, Router } from '@angular/router';
import { SearchInputComponent } from '@digitaldelightsoy/design-library/src/input';
import { BindingsDictionary, HotkeysService, Unlisten } from '@digitaldelightsoy/design-library/src/services';
import { Subject, filter, takeUntil } from 'rxjs';

export interface TopbarConfiguration {
    /**
     * Renders a text next to the menu button on larger screens.
     */
    menuText?: string;

    /**
     * Topbar logo configuration.
     * If configuration is not provided, logo is not rendered.
     */
    logo?: TopbarLogo;

    /**
     * Topbar user button configuration.
     * If configuration is not provided, user button is not rendered.
     */
    user?: TopbarUser;

    /**
     * Topbar additional buttons configuration.
     * If configuration is not provided, additional buttons are not rendered.
     *
     * Only one button can be shown on smallest mobile screen size.
     */
    additionalButtons?: TopbarAdditionalButton[];

    /**
     * Topbar search configuration.
     * If configuration is not provided, search is not rendered.
     */
    search?: TopbarSearchConfiguration;
}

export interface TopbarSearchConfiguration {
    /**
     * Placeholder text for the search input.
     */
    searchPlaceholder: string;

    /**
     * Output event when user has searched for something.
     * Emits a list that contains the search string and references the the search container.
     */
    searchEvent: (searchText: string, searchContainer: HTMLElement) => void;

    /**
     * List of hotkeys that are used to trigger the search.
     */
    hotkeyList?: { key: string; ctrlKey?: boolean; altKey?: boolean; shiftKey?: boolean }[];
}

/**
 * Topbar user button configuration.
 */
export interface TopbarUser {
    /**
     * First name of the user.
     * Names are split to two so initials can be rendered.
     */
    firstName: string;

    /**
     * Last name of the user.
     * Names are split to two so initials can be rendered.
     */
    lastName: string;

    /**
     * Description text for the user button.
     * This is rendered under the user name.
     */
    description?: string;

    /**
     * Image url for the user button.
     * If not provided, the user button is rendered with initials of the user.
     */
    imageUrl?: string;

    /**
     * Click event for the user button.
     */
    onClick: () => void;
}

/**
 * Renders buttons on the right side of the top bar.
 * The buttons are placed on the left side of the user button.
 */
export interface TopbarAdditionalButton {
    /**
     * Icon for the button.
     */
    icon: string;

    /**
     * Color of the badge.
     * If not provided, the defaults to danger color.
     */
    badgeColor?: string;

    /**
     * Shows a badge with the given text.
     * If not provided, the badge is not rendered.
     */
    badgeText?: string;

    /**
     * If the button should be shown on small screens mobile.
     * Only the first button where this is true is shown on mobile.
     */
    showOnMobile?: boolean;

    /**
     * Click event for the topbar button.
     */
    onClick: () => void;
}

/**
 * Renders a logo to the top bar.
 * The logo is rendered on the left side of the top bar.
 * The logo will always route to the home ('/' route) page.
 */
export interface TopbarLogo {
    /**
     * Logo url.
     */
    url: string;

    /**
     * Color of the background of the logo.
     */
    backgroundColor?: string;

    /**
     * Title is rendered on the right side of the logo.
     */
    title?: string;

    /**
     * Small text rendered on top of the logo.
     */
    additionalText?: string;
}

@Component({
    selector: 'dd-toolbar-top',
    templateUrl: './toolbar-top.component.html',
    styleUrls: ['./toolbar-top.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ToolbarTopComponent implements OnInit, OnDestroy {
    constructor(private cdr: ChangeDetectorRef, private hotkeys: HotkeysService, router: Router) {
        // Make sure the mobile search is closed when navigation happens.
        router.events
            .pipe(
                filter((event) => event instanceof NavigationEnd),
                takeUntil(this._unsubscribeAllSubject)
            )
            .subscribe(() => {
                this.showMobileSearch = false;
            });
    }

    /** Hides the top bar button. */
    @Input() hideButton: boolean = false;

    /**
     * Configuration for the top bar.
     */
    @Input() configuration?: TopbarConfiguration;

    @Output() menuClick: EventEmitter<void> = new EventEmitter();

    @ViewChild(SearchInputComponent, { static: false }) private searchInput?: SearchInputComponent;

    showMobileSearch: boolean = false;

    private unlisten?: Unlisten;
    private _unsubscribeAllSubject: Subject<undefined> = new Subject();

    ngOnInit(): void {
        // If there are hotkeys then create the listener for them.
        if (this.configuration?.search?.hotkeyList?.length) {
            const bindings: BindingsDictionary = {};

            // Go through all the hotkeys and create bindings for them.
            this.configuration.search.hotkeyList.forEach((hotkey) => {
                bindings[hotkey.key] = (event: KeyboardEvent) => {
                    if (hotkey.ctrlKey && !event.ctrlKey) {
                        return;
                    }
                    if (hotkey.altKey && !event.altKey) {
                        return;
                    }
                    if (hotkey.shiftKey && !event.shiftKey) {
                        return;
                    }

                    event.preventDefault();
                    this.searchInput?.searchInputElement?.nativeElement.focus();
                };
            });

            // Create the listener for the hotkeys.
            this.unlisten = this.hotkeys.listen(bindings, {
                priority: 99,
                emitInputs: true,
            });
        }
    }

    ngOnDestroy(): void {
        this.unlisten?.();
    }

    /**
     * Get user initials from the given user.
     */
    get userInitials(): string {
        if (!this.configuration?.user) {
            return '';
        }

        return `${this.configuration.user.firstName[0]}${this.configuration.user.lastName[0]}`.trim();
    }

    /**
     * Get the index of the first additional button that should be shown on mobile.
     */
    get additionalMobileButtonIndex(): number | undefined {
        if (!this.configuration?.additionalButtons) {
            return -1;
        }

        return this.configuration.additionalButtons.findIndex((button) => button.showOnMobile);
    }

    /**
     * Toggles the mobile search element.
     */
    toggleMobileSearch(): void {
        this.showMobileSearch = !this.showMobileSearch;

        this.cdr.markForCheck();
    }
}

