import { Injectable } from '@angular/core';
import { Locale } from 'date-fns';
import { da, enGB, fi, nb, sv } from 'date-fns/locale';
import { BehaviorSubject, Observable, Subject, distinctUntilChanged, map } from 'rxjs';

export type DdLocale = 'dk' | 'no' | 'fi' | 'se' | 'en';
export type DdCulture = 'da-dk' | 'nb-no' | 'fi-fi' | 'sv-se' | 'en-gb';

@Injectable({
    providedIn: 'root',
})
export class DdLocaleService {
    constructor() {}

    /**
     * Subjects that stores the current locale.
     */
    private localeSubject: Subject<DdLocale> = new BehaviorSubject<DdLocale>('en');

    /**
     * Updates the locale for the service.
     */
    updateLocale(l: DdLocale): void {
        this.localeSubject.next(l);
    }

    /**
     * Exposes locale changes property from the service.
     */
    localeChanges$(): Observable<DdLocale> {
        return this.localeSubject.asObservable().pipe(distinctUntilChanged());
    }

    /**
     * Gets the current date-fns Locale.
     */
    getDateLocale$(): Observable<Locale> {
        return this.localeChanges$().pipe(
            map((l) => {
                switch (l) {
                    case 'dk':
                        return da;
                    case 'no':
                        return nb;
                    case 'fi':
                        return fi;
                    case 'se':
                        return sv;
                    case 'en':
                    default:
                        return enGB;
                }
            })
        );
    }

    /**
     * Gets current local culture for the pipes.
     */
    getLocalCulture$(): Observable<DdCulture> {
        return this.localeChanges$().pipe(
            map((l) => {
                switch (l) {
                    case 'dk':
                        return 'da-dk';
                    case 'no':
                        return 'nb-no';
                    case 'fi':
                        return 'fi-fi';
                    case 'se':
                        return 'sv-se';
                    case 'en':
                    default:
                        return 'en-gb';
                }
            })
        );
    }

    /**
     * Gets date format for the pipes.
     */
    getDateFormat(l: Locale): string {
        // DK has specific format as date-fns does not have the same as we use.
        if (l.code === 'da') {
            return 'dd-MM-yyyy';
        }

        return 'P';
    }

    /**
     * Gets date time format for the pipes.
     */
    getDateTimeFormat(l: Locale): string {
        // DK has specific format as date-fns does not have the same as we use.
        if (l.code === 'da') {
            return 'dd-MM-yyyy p';
        }

        return 'Pp';
    }
}

import { Injectable, NgZone } from '@angular/core';
import { BindingsDictionary, Handler, keyEvents, Listener, ListenerOptions, Unlisten } from './models/keyboard';

/**
 * https://github.com/bennadel/JavaScript-Demos/tree/master/demos/global-key-combos-angular5
 */
@Injectable({
    providedIn: 'root',
})
export class HotkeysService {
    constructor(private zone: NgZone) {
        // Since we're going to create a root event-handler for the keydown event, we're
        // gonna do this outside of the NgZone. This way, we're not constantly triggering
        // change-detection for every key event - we'll only re-enter the Angular Zone
        // when we have an event that is actually being consumed by one of our components.
        this.zone.runOutsideAngular((): void => {
            window.addEventListener(keyEvents.keydown, this.handleKeyboardEvent);
        });
    }

    private listeners: Listener[] = [];

    /**
     * Configure key-event listener at the given priority.
     * Returns a Function that can be used to unbind the listener.
     */
    listen(bindings: BindingsDictionary, options: ListenerOptions): Unlisten {
        const listener: Listener = this.addListener({
            terminal: this.normalizeTerminal(options.terminal),
            priority: options.priority,
            bindings,
            emitInputs: options.emitInputs || false,
        });

        const unlisten: Unlisten = (): void => {
            this.removeListener(listener);
        };

        return unlisten;
    }

    /**
     * Add the listener to the internal collection in DESCENDING priority order.
     */
    private addListener(listener: Listener): Listener {
        this.listeners.push(listener);

        // Sort with higher priority first.
        this.listeners.sort((a: Listener, b: Listener): number => {
            if (a.priority < b.priority) {
                return 1;
            } else if (a.priority > b.priority) {
                return -1;
            } else {
                return 0;
            }
        });

        return listener;
    }

    /**
     * Handle the keyboard events for the root handler (and delegate to the listeners).
     */
    private handleKeyboardEvent = (event: KeyboardEvent): void => {
        event.stopImmediatePropagation();

        const key: string = event.key;
        const isInputEvent: boolean = this.isEventFromInput(event);
        let handler: Handler;

        // Iterate over the listeners.
        for (const listener of this.listeners) {
            if ((handler = listener.bindings[key])) {
                // Execute handler if this is NOT an input event that we need to ignore.
                if (isInputEvent && !listener.emitInputs) {
                    return;
                }

                // Right now, we're executing outside of the NgZone. As such, we
                // have to re-enter the NgZone so that we can hook back into change-
                // detection. Plus, this will also catch errors and propagate them
                // through application properly.
                this.zone.runGuarded((): boolean | void => handler(event));

                // Break loop if we have terminal listener.
                if (listener.terminal) {
                    return;
                }
            }
        }
    };

    /**
     * Determine if the given event originated from a form input element.
     */
    private isEventFromInput(event: KeyboardEvent): boolean {
        if (event.target instanceof Node) {
            switch (event.target.nodeName) {
                case 'INPUT':
                case 'SELECT':
                case 'TEXTAREA':
                    return true;
                default:
                    return false;
            }
        }

        return false;
    }

    /**
     * Normalize the terminal option.
     */
    private normalizeTerminal(terminal: boolean | undefined): boolean {
        if (terminal === undefined) {
            return true;
        }

        return terminal;
    }

    /**
     * Remove the given listener from the internal collection.
     */
    private removeListener(listenerToRemove: Listener): void {
        this.listeners = this.listeners.filter((listener: Listener): boolean => listener !== listenerToRemove);
    }
}

import { FlexibleConnectedPositionStrategyOrigin, NoopScrollStrategy, Overlay, OverlayConfig, OverlayRef } from '@angular/cdk/overlay';
import { ComponentPortal, ComponentType } from '@angular/cdk/portal';
import { ComponentRef, Injectable, Injector, StaticProvider } from '@angular/core';
import { keyboardKeys } from '@digitaldelightsoy/design-library/src/services';
import { Observable, Subject } from 'rxjs';
import { filter, take, takeUntil } from 'rxjs/operators';
import { animationOutTime } from '../models/animations';
import { DialogOverlaySize, IDataOverlay, OVERLAY_DATA, OVERLAY_REF, OverlayOpenModel, OverlayProviderConfig, OverlaySize } from '../models/overlay';

@Injectable({
    providedIn: 'root',
})
export class OverlayService {
    constructor(private overlay: Overlay, private _injector: Injector) {}

    private _unsubscribeSubject: Subject<void> = new Subject();

    /**
     * Opens an overlay with the provided component and config.
     * Makes sure that the opened component actually contains a data property.
     */
    open<T extends IDataOverlay<E>, E>(component: ComponentType<T>, config: OverlayProviderConfig<E>): OverlayOpenModel<T> {
        // Create new overlay attached to the input element.
        const overLayRef: OverlayRef = this.overlay.create(config.overlayConfig);

        // Create injector for the data
        let injector: Injector | undefined;
        if (!!config?.data) {
            injector = this.createInjector(overLayRef, config);
        }

        let obs$: Observable<MouseEvent> | undefined;
        if (config.closeOnOutsidePointerEvents) {
            obs$ = overLayRef.outsidePointerEvents();
        } else if (config.closeOnBackdropClick) {
            obs$ = overLayRef.backdropClick();
        }

        // Pressing escape should always close the current overlay.
        overLayRef
            .keydownEvents()
            .pipe(
                filter((event) => event.key === keyboardKeys.escape),
                take(1),
                takeUntil(this._unsubscribeSubject)
            )
            .subscribe(() => {
                this.close(overLayRef, componentRef.instance);
            });

        // Attach component to the overlay
        const componentRef: ComponentRef<T> = overLayRef.attach(new ComponentPortal(component, undefined, injector));

        // Add detach event for click outside the overlay.
        obs$?.pipe(take(1), takeUntil(this._unsubscribeSubject)).subscribe(() => {
            this.close(overLayRef, componentRef.instance);
        });

        return {
            overlayRef: overLayRef,
            component: componentRef.instance,
            close: () => {
                this.close(overLayRef, componentRef.instance);
            },
        };
    }

    /**
     * Config for an overlay connected to an element.
     * Requires class string of the provided overlay as the transformOrigin parameter.
     */
    getConnectedToConfig<T>(element: FlexibleConnectedPositionStrategyOrigin, transformOrigin: string, data?: T | undefined): OverlayProviderConfig<T> {
        if (!element) {
            throw Error('Missing ElementRef input.');
        }

        const config: OverlayProviderConfig<T> = {
            data,
            closeOnOutsidePointerEvents: true,
            overlayConfig: new OverlayConfig({
                positionStrategy: this.overlay
                    .position()
                    .flexibleConnectedTo(element)
                    .withTransformOriginOn(transformOrigin)
                    .withPositions([
                        /**
                         * Provide alternative positions in case element does not fit in viewport. Configures that the position strategy should set a `transform-origin` on some elements
                         * inside the overlay, depending on the current position that is being applied. This is
                         * useful for the cases where the origin of an animation can change depending on the
                         * alignment of the overlay.
                         * https://material.angular.io/cdk/overlay/api#CdkConnectedOverlay
                         */
                        {
                            // top-left of the overlay is connected to bottom-left of the element
                            originX: 'start',
                            originY: 'bottom',
                            overlayX: 'start',
                            overlayY: 'top',
                        },
                        {
                            // Bottom-left of the overlay is connected to top-left of the element.
                            originX: 'start',
                            originY: 'top',
                            overlayX: 'start',
                            overlayY: 'bottom',
                        },

                        {
                            // Top-right of the overlay is connected to bottom-right of the element.
                            originX: 'end',
                            originY: 'bottom',
                            overlayX: 'end',
                            overlayY: 'top',
                        },
                        {
                            // Bottom-right of the overlay is connected to top-right of the element.
                            originX: 'end',
                            originY: 'top',
                            overlayX: 'end',
                            overlayY: 'bottom',
                        },
                    ])
                    .withPush(false),
                hasBackdrop: false,
                disposeOnNavigation: true,
                scrollStrategy: this.overlay.scrollStrategies.reposition(),
            }),
        };
        return config;
    }

    /**
     * Config for a global centered flex overlay.
     * Flex overlay defines only max width and height based on the input.
     */
    getFlexGlobalCenterConfig<T>(variant: OverlaySize, data?: T | undefined): OverlayProviderConfig<T> {
        const [width, _]: [string, string] = this.getOverlaySizes(variant);

        const config: OverlayProviderConfig<T> = {
            data,
            closeOnBackdropClick: true,
            overlayConfig: new OverlayConfig({
                positionStrategy: this.overlay
                    .position() //
                    .global()
                    .centerHorizontally()
                    .centerVertically(),
                hasBackdrop: true,
                scrollStrategy: this.overlay.scrollStrategies.block(),
                panelClass: ['dd-overlay-container', 'flex', variant],
                maxWidth: width,
            }),
        };

        return config;
    }

    /** Config for a global centered overlay. */
    getGlobalCenterConfig<T>(variant: OverlaySize, data?: T | undefined): OverlayProviderConfig<T> {
        const [width, height]: [string, string] = this.getOverlaySizes(variant);

        const config: OverlayProviderConfig<T> = {
            data,
            closeOnBackdropClick: true,
            overlayConfig: new OverlayConfig({
                positionStrategy: this.overlay
                    .position() //
                    .global()
                    .centerHorizontally()
                    .centerVertically(),
                hasBackdrop: true,
                scrollStrategy: this.overlay.scrollStrategies.block(),
                panelClass: ['dd-overlay-container', variant],
                width,
                maxWidth: width,
                height,
                maxHeight: height,
                disposeOnNavigation: true,
            }),
        };

        return config;
    }

    /** Config for a global centered overlay that is fully scrollable on mobile. */
    getGlobalScrollableCenterConfig<T>(variant: OverlaySize, data?: T | undefined): OverlayProviderConfig<T> {
        const [width]: [string, string] = this.getOverlaySizes(variant);

        const config: OverlayProviderConfig<T> = {
            data,
            closeOnBackdropClick: true,
            overlayConfig: new OverlayConfig({
                positionStrategy: this.overlay
                    .position() //
                    .global()
                    .centerHorizontally()
                    .centerVertically(),
                hasBackdrop: true,
                scrollStrategy: this.overlay.scrollStrategies.block(),
                panelClass: ['dd-overlay-container', 'scrollable', variant],
                width,
                maxWidth: width,
                maxHeight: '100dvh',
                disposeOnNavigation: true,
            }),
        };

        return config;
    }

    /** Config for a globally centered confirm selection overlay. */
    getConfirmSelectionOverlayConfig<T>(data?: T | undefined): OverlayProviderConfig<T> {
        const [width, height]: [string, string] = ['365px', '220px'];

        const config: OverlayProviderConfig<T> = {
            data,
            closeOnBackdropClick: true,
            overlayConfig: new OverlayConfig({
                positionStrategy: this.overlay
                    .position() //
                    .global()
                    .centerHorizontally()
                    .centerVertically(),
                hasBackdrop: true,
                scrollStrategy: this.overlay.scrollStrategies.block(),
                panelClass: ['dd-overlay-container'],
                width,
                maxWidth: width,
                minHeight: height,
                disposeOnNavigation: true,
            }),
        };

        return config;
    }

    /** Config for a dialog overlay opening from the right. */
    getDialogOverlayConfig<T>(variant: DialogOverlaySize, data?: T | undefined): OverlayProviderConfig<T> {
        const width: string = this.getDialogOverlaySizes(variant);

        const config: OverlayProviderConfig<T> = {
            data,

            closeOnBackdropClick: true,
            overlayConfig: new OverlayConfig({
                positionStrategy: this.overlay
                    .position() //
                    .global()
                    .right(),
                hasBackdrop: true,
                scrollStrategy: this.overlay.scrollStrategies.block(),
                panelClass: ['dd-dialog-container', variant],
                width: '100%',
                maxWidth: width,
                height: '100vh',
                disposeOnNavigation: true,
            }),
        };

        return config;
    }
    /** Returns the width and height values for the overlay configurations. */
    private getOverlaySizes(variant: OverlaySize): [string, string] {
        switch (variant) {
            case 'small':
                return ['288px', '264px'];
            case 'medium':
                return ['576px', '528px'];
            default:
                return ['864px', '792px'];
        }
    }

    /** Returns the width and height values for the overlay configurations. */
    private getDialogOverlaySizes(variant: DialogOverlaySize): string {
        switch (variant) {
            case 'dialog-mega':
                return '864px';
            case 'dialog-wide':
                return '640px';
            case 'dialog-narrow':
                return '320px';
            default:
                return '864px';
        }
    }

    /**
     * Create injector to pass data to overlay.
     */
    private createInjector(overlayRef: OverlayRef, config: OverlayProviderConfig): Injector {
        const providers: StaticProvider[] = [
            { provide: OVERLAY_REF, useValue: overlayRef }, //
            { provide: OVERLAY_DATA, useValue: config.data },
        ];

        return Injector.create({
            parent: this._injector,
            providers,
        });
    }

    /** Handles closing and removing the overlay */
    private close(overLayRef: OverlayRef, component?: any): void {
        // Detach and remove overlay
        if (!overLayRef) {
            return;
        }

        // Updating scroll strategy before closing makes sure when there is a redirect the scroll is returned to top.
        // Keeping block scroll strategy causes the scroll amount to stay the same even after redirect when overlay has an animation.
        overLayRef.updateScrollStrategy(new NoopScrollStrategy());

        // For the overlay animation, if a viewState is set, then a timeout should be set to show out animation.
        if (!component.viewState) {
            overLayRef.detach();
        } else {
            component.viewState = 'hide';
            setTimeout(() => {
                overLayRef.detach();
            }, animationOutTime);
        }
    }
}

import { Injectable } from '@angular/core';
import { delay, Observable, ReplaySubject, Subject, take, timer } from 'rxjs';
import { SnackBar } from '../models/notifications-models';

@Injectable({
    providedIn: 'root',
})
export class SnackBarService {
    private notifications: SnackBar[] = [];
    private notificationSubject: Subject<SnackBar[]> = new ReplaySubject();

    private id: number = 0;

    /**
     * Time in milliseconds that is reserved for the animation to execute.
     * This should match the css animation time in the SnackBarComponent.
     */
    private readonly animationTimeMs: number = 400;

    getNotifications$(): Observable<SnackBar[]> {
        return this.notificationSubject.asObservable();
    }

    /**
     * Shows message in snackbar and return a reference to the snackbar.
     */
    showMessage(message: string): void {
        this.addNotification(message);
    }

    /**
     * Show success message in snackbar and return a reference to the snackbar.
     */
    showSuccessMessage(message: string): void {
        this.addNotification(message, 'success');
    }

    /**
     * Shows error message in snackbar and return a reference to the snackbar.
     */
    showErrorMessage(message: string): void {
        this.addNotification(message, 'error');
    }

    private addNotification(message: string, style?: 'error' | 'success'): void {
        const newNotification: SnackBar = {
            id: this.id++,
            text: message,
            timeout: 4000,
            classes: [],
        };

        if (style) {
            newNotification.classes.push(style);
        }

        if (newNotification.timeout) {
            // Close notification after timeout
            timer(newNotification.timeout)
                .pipe(
                    // Add some time for the animation.
                    delay(this.animationTimeMs),
                    take(1)
                )
                .subscribe((_) => {
                    this.close(newNotification.id);
                });
        }

        this.notifications.push(newNotification);
        this.refreshNotifications(this.notifications);
    }

    close(id: number): void {
        const notification: SnackBar | undefined = this.notifications.find((x) => x.id === id);
        // Check if notification has already been closed.
        if (!notification) {
            return;
        }

        // Start closing animation by adding a class.
        notification.classes.push('dismiss');
        // Switch the boolean to trigger change detection from the timer.
        // For some reason adding stuff to a list does not trigger ChangeDetectionStrategy.OnPush inside a timeout scope.
        notification.dismiss = true;
        this.refreshNotifications(this.notifications);

        // Actually remove after closing animation finishes.
        setTimeout(() => {
            const notifications = this.notifications.filter((x) => x.id !== id);
            this.refreshNotifications(notifications);
        }, this.animationTimeMs);
    }

    private refreshNotifications(notifications: SnackBar[]): void {
        // Always show the latest notification at the bottom.
        notifications.sort((a, b) => b.id - a.id);

        // Refresh
        this.notifications = notifications;
        this.notificationSubject.next(notifications);
    }
}

