import { AbstractControl, ValidationErrors, ValidatorFn } from '@angular/forms';

export interface FileErrorMessages {
    required?: string;
    uniqueFileNames?: string;
    fileExtension?: string;
    maxFileSize?: string;
}

export class FileValidators {
    static fileExtension(ext: string[]): ValidatorFn {
        return (control: AbstractControl): ValidationErrors | null => {
            if (control.value) {
                const validExtensions = ext.map((e) => e.trim().toLowerCase());
                const fileArray = control.value as File[];

                const invalidFiles = fileArray
                    .map((file) => file.name)
                    .filter((fname) => {
                        const extension = fname.slice(((fname.lastIndexOf('.') - 1) >>> 0) + 2).toLowerCase();
                        return !validExtensions.includes(extension);
                    })
                    .map((name) => ({ name, ext: name.slice(((name.lastIndexOf('.') - 1) >>> 0) + 2) }));

                return !invalidFiles.length ? null : { fileExtension: { requiredExtension: ext.toString(), actualExtensions: invalidFiles } };
            } else {
                return null;
            }
        };
    }

    static uniqueFileNames(control: AbstractControl): ValidationErrors | null {
        if (control.value) {
            const fileNameArray = (control.value as File[]).map((file) => file.name);

            const duplicates = fileNameArray.reduce((acc, curr) => {
                acc[curr] = acc[curr] ? acc[curr] + 1 : 1;
                return acc;
            }, {} as any);

            const duplicatesArray: { name: string; count: number }[] = (Object.entries(duplicates) as [string, number][]).filter((arr) => arr[1] > 1).map((arr) => ({ name: arr[0], count: arr[1] }));

            return !duplicatesArray.length ? null : { uniqueFileNames: { duplicatedFileNames: duplicatesArray } };
        } else {
            return null;
        }
    }

    static maxFileSize(bytes: number): ValidatorFn {
        return (control: AbstractControl): ValidationErrors | null => {
            if (control.value) {
                const fileArray = control.value as File[];

                const invalidFiles: { name: string; size: number }[] = fileArray.filter((file) => file.size > bytes).map((file) => ({ name: file.name, size: file.size }));

                return !invalidFiles.length ? null : { maxFileSize: { maxSize: bytes, actualSizes: invalidFiles } };
            } else {
                return null;
            }
        };
    }

    static required(control: AbstractControl): ValidationErrors | null {
        const count = control?.value?.length;
        return count ? null : { required: true };
    }
}

import { OverlayConfig, OverlayRef } from '@angular/cdk/overlay';
import { InjectionToken } from '@angular/core';

export const OVERLAY_DATA: InjectionToken<{}> = new InjectionToken<{}>('OverlayData');
export const OVERLAY_REF: InjectionToken<{}> = new InjectionToken<OverlayRef>('OverlayRef');

/**
 * Opening an overlay returns a interface that wraps all necessary functionalities for opening and closing the overlay,
 * in addition to handling subscriptions necessary for the observable functions.
 */
export interface OverlayOpenModel<T> {
    overlayRef: OverlayRef;
    /** Reference to the component instance so input data can be bound to the component.  */
    component: T;

    /** Closes the overlay. */
    close: () => void;
}

export class OverlayProviderConfig<T = any> {
    /** Data to be injected into the dialog content. */
    data?: T;

    /** Automatically add subscription to close on backdrop click. */
    closeOnBackdropClick?: boolean;

    /** Automatically add subscription to close on outside pointer events. */
    closeOnOutsidePointerEvents?: boolean;

    overlayConfig?: OverlayConfig;
}

export type OverlaySize = 'small' | 'medium' | 'large';
export type DialogOverlaySize = 'dialog-wide' | 'dialog-narrow' | 'dialog-mega';

/**
 * Data that is passed to the overlay.
 */
export interface IDataOverlay<T> {
    readonly data: T;
}

