import { Directive, EventEmitter, HostBinding, HostListener, Input, Output } from '@angular/core';

@Directive({
    standalone: true,
    // eslint-disable-next-line @angular-eslint/directive-selector
    selector: '[ddFileDragDrop]',
})
export class DragDropDirective {
    constructor() {}
    @Input('ddFileDragDrop') dragAndDrop?: boolean;
    @HostBinding('class.fileover') dragOver?: boolean;

    @Output() fileDropped: EventEmitter<File[]> = new EventEmitter();

    @HostListener('dragover', ['$event']) onDragOver(evt: Event): void {
        this.dragOver = true;

        evt.preventDefault();
        evt.stopPropagation();
    }

    @HostListener('dragleave', ['$event']) public onDragLeave(evt: Event): void {
        this.dragOver = false;

        evt.preventDefault();
        evt.stopPropagation();
    }

    @HostListener('drop', ['$event']) public onDrop(evt: any): void {
        evt.preventDefault();
        evt.stopPropagation();

        this.dragOver = false;
        const files = evt.dataTransfer.files;

        if (files.length > 0) {
            this.fileDropped.emit(files);
        }
    }
}

import { Directive, ElementRef, HostListener, Input, OnDestroy, Renderer2 } from '@angular/core';

export type TooltipPosition = 'top' | 'bottom' | 'left' | 'right';

@Directive({
    // eslint-disable-next-line @angular-eslint/directive-selector
    selector: '[ddTooltip]',
})
export class TooltipDirective implements OnDestroy {
    constructor(private el: ElementRef, private renderer: Renderer2) {}

    @Input('ddTooltip') tooltipTitle?: string;
    @Input() placement?: TooltipPosition = 'top';
    @Input() sticky?: boolean = false;
    private delayMs: number = 30;
    private offset: number = 10;
    private tooltip?: HTMLElement;
    private destroyCallback?: () => void;

    @HostListener('mouseenter') onMouseEnter(): void {
        if (!this.tooltipTitle) {
            return;
        }

        if (!this.tooltip) {
            this.show();
        }
    }

    @HostListener('mouseleave') onMouseLeave(): void {
        if (!this.tooltipTitle) {
            return;
        }

        if (this.tooltip) {
            this.hide();
        }
    }

    ngOnDestroy(): void {
        // this.tooltip is undefined here for some reason. Reference is found in the destroycallback
        if (this.destroyCallback) {
            this.destroyCallback();
        }
    }

    /** Creates tooltip, sets correct position and then shows it. */
    private show(): void {
        this.create();
        this.setPosition();

        this.renderer.addClass(this.tooltip, 'dd-tooltip-show');
    }

    /** Hides tooltip. */
    private hide(): void {
        this.renderer.removeClass(this.tooltip, 'dd-tooltip-show');
        this.renderer.removeChild(document.body, this.tooltip);

        delete this.tooltip;
        delete this.destroyCallback;
    }

    private create(): void {
        if (!this.tooltipTitle) {
            throw Error('Tooltip title missing');
        }

        this.tooltip = this.renderer.createElement('span');

        this.destroyCallback = () => {
            this.renderer.removeChild(document.body, this.tooltip);
            delete this.destroyCallback;
        };

        this.renderer.appendChild(
            this.tooltip,
            this.renderer.createText(this.tooltipTitle) // textNode
        );

        this.renderer.appendChild(document.body, this.tooltip);

        this.renderer.addClass(this.tooltip, 'dd-tooltip');
        this.renderer.addClass(this.tooltip, `dd-tooltip-${this.placement}`);
        if (this.sticky) {
            this.renderer.addClass(this.tooltip, 'sticky');
        }

        this.renderer.setStyle(this.tooltip, '-webkit-transition', `opacity ${this.delayMs}ms`);
        this.renderer.setStyle(this.tooltip, '-moz-transition', `opacity ${this.delayMs}ms`);
        this.renderer.setStyle(this.tooltip, '-o-transition', `opacity ${this.delayMs}ms`);
        this.renderer.setStyle(this.tooltip, 'transition', `opacity ${this.delayMs}ms`);
    }

    private setPosition(): void {
        if (!this.tooltip) {
            throw Error('Tooltip not found.');
        }

        const hostPos = this.el.nativeElement.getBoundingClientRect();

        const tooltipPos: DOMRect = this.tooltip.getBoundingClientRect();

        const scrollPos = this.sticky ? 0 : window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0;

        let top;
        let left;

        if (this.placement === 'top') {
            top = hostPos.top - tooltipPos.height - this.offset;
            left = hostPos.left + (hostPos.width - tooltipPos.width) / 2;
        }

        if (this.placement === 'bottom') {
            top = hostPos.bottom + this.offset;
            left = hostPos.left + (hostPos.width - tooltipPos.width) / 2;
        }

        if (this.placement === 'left') {
            top = hostPos.top + (hostPos.height - tooltipPos.height) / 2;
            left = hostPos.left - tooltipPos.width - this.offset;
        }

        if (this.placement === 'right') {
            top = hostPos.top + (hostPos.height - tooltipPos.height) / 2;
            left = hostPos.right + this.offset;
        }

        this.renderer.setStyle(this.tooltip, 'top', `${top + scrollPos}px`);
        this.renderer.setStyle(this.tooltip, 'left', `${left}px`);
    }
}

