import { animate, state, style, transition, trigger } from '@angular/animations';
import { ChangeDetectionStrategy, Component, EventEmitter, Input, Output, TemplateRef } from '@angular/core';

/**
 * Provides an interactive UI component that can be expanded or collapsed.
 */
@Component({
    selector: 'dd-accordion',
    templateUrl: './accordion.component.html',
    styleUrls: ['./accordion.component.scss'],
    animations: [
        trigger('toggleAccordionAnimation', [
            /** Define the animation for expanding the accordion. */
            transition(':enter', [style({ 'max-height': '0px' }), animate('150ms linear', style({ 'max-height': '99999px' }))]),
            /** Define the animation for collapsing the accordion. */
            transition(':leave', [style({ 'max-height': '*' }), animate('150ms linear', style({ 'max-height': '0px' }))]),
            state(
                'open',
                style({
                    overflow: 'auto',
                })
            ),
        ]),
    ],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class AccordionComponent {
    /** The header text of the accordion. */
    @Input() header?: string;
    /** The subheader text of the accordion. */
    @Input() subHeader?: string;
    /**
     * Whether to pre-render the content of the accordion.
     * This can be useful for performance reasons if the content is complex.
     */
    @Input() preRenderContent?: boolean = false;
    /** Controls if the accordion is open/closed. */
    @Input() open: boolean = false;
    /** The background color of the accordion header. */
    @Input() headerBackground?: 'dark' | 'light' = 'dark';
    /**
     * A template that can be passed as an input.
     * For instance, this is used with external libraries when preRenderContent is false,
     * because some do not work properly inside ng-content.
     */
    @Input() template?: TemplateRef<any>;

    /** An event that is emitted when the accordion is clicked. */
    @Output() accordionClick: EventEmitter<void> = new EventEmitter();

    /**
     * Returns an object that maps class names to boolean values.
     * These classes are used to style the accordion based on its state.
     */
    get accordionClasses(): { [klass: string]: boolean } {
        return {
            'elevation-04': this.open,
            'elevation-00': !this.open,
        };
    }

    /**
     * Returns an object that maps class names to boolean values.
     * These classes are used to style the accordion header based on its state.
     */
    get accordionHeaderClasses(): { [klass: string]: boolean } {
        return {
            'elevation-04 border-radius-0-bottom accordion-header-open ': this.open,
            'dd-primary-500 dd-primary-600-hover': this.headerBackground === 'dark' && this.open,
            'dd-primary-50 dd-primary-100-hover': this.headerBackground === 'light' && this.open,
            'dd-white dd-primary-50-hover': !this.open,
        };
    }
}

