import { animate, state, style, transition, trigger } from '@angular/animations';
import { ChangeDetectionStrategy, Component, EventEmitter, Input, Output } from '@angular/core';

/**
 * Provides an interactive UI component that can be expanded or collapsed.
 */
@Component({
    selector: 'dd-accordion-v2',
    templateUrl: './accordion-v2.component.html',
    styleUrls: ['./accordion-v2.component.scss'],
    animations: [
        trigger('toggleAccordionAnimation', [
            /** Define the animation for expanding the accordion. */
            transition(':enter', [style({ 'max-height': '0px' }), animate('150ms linear', style({ 'max-height': '99999px' }))]),
            /** Define the animation for collapsing the accordion. */
            transition(':leave', [style({ 'max-height': '*' }), animate('150ms linear', style({ 'max-height': '0px' }))]),
            state(
                'open',
                style({
                    overflow: 'auto',
                })
            ),
        ]),
    ],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class AccordionV2Component {
    /** Controls if the accordion is open/closed. */
    @Input() open: boolean = false;
    /** The side color of the accordion. */
    @Input() sideColor?: string;
    /** The background color of the accordion header. */
    @Input() headerBackground?: 'white' | 'light' = 'white';

    /** An event that is emitted when the accordion is clicked. */
    @Output() accordionClick: EventEmitter<void> = new EventEmitter();

    /**
     * Returns an object that maps class names to boolean values.
     * These classes are used to style the accordion based on its state.
     */
    get accordionClasses(): { [klass: string]: boolean } {
        return {
            'elevation-04': this.open,
            'elevation-00': !this.open,
        };
    }

    /**
     * Returns an object that maps class names to boolean values.
     * These classes are used to style the accordion header based on its state.
     */
    get accordionHeaderClasses(): { [klass: string]: boolean } {
        return {
            'accordion-header-open border-radius-0-bottom': this.open && this.headerBackground === 'white',
            'dd-primary-50-bg dd-primary-100-hover': this.open && this.headerBackground === 'light',
            'dd-primary-50-hover': !this.open || this.headerBackground === 'white',
        };
    }

    /**
     * Returns an object that maps class names to boolean values.
     * These classes are used to style the side color of the accordion based on its state.
     */
    get sideColorClasses(): { [klass: string]: boolean } {
        const classDict: { [klass: string]: boolean } = {
            'border-radius-0-bottom': this.open,
        };

        if (!this.sideColor) {
            return classDict;
        }

        classDict[this.sideColor] = true;

        return classDict;
    }
}

