import { ChangeDetectionStrategy, Component, Input } from '@angular/core';
import { OverlayOpenModel, OverlayService } from '@digitaldelightsoy/design-library/src/overlay';
import { Subject, delay, take, takeUntil } from 'rxjs';
import { BadgeInfoOverlayComponent } from '../badge-info-overlay/badge-info-overlay.component';
import { BadgeComponent } from '../badge/badge.component';

/**
 * Extends the BadgeComponent to include additional information in a tooltip.
 */
@Component({
    selector: 'dd-badge-info',
    templateUrl: './badge-info.component.html',
    styleUrls: ['./badge-info.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class BadgeInfoComponent extends BadgeComponent {
    constructor(private overlayService: OverlayService) {
        super();
    }

    /**
     * Text to be shown in the tooltip.
     */
    @Input() infoText?: string;

    overlayModel?: OverlayOpenModel<BadgeInfoOverlayComponent>;

    private readonly _unsubscribeAllSubject: Subject<undefined> = new Subject();

    /**
     * Handles the click event on the badge.
     * If the tooltip is open, it closes it.
     * If the tooltip is not open, it opens it.
     */
    onAnchorClick(element: HTMLDivElement): void {
        // Close existing overlay
        if (!!this.overlayModel) {
            this.closeMenu();
            return;
        }

        if (!this.infoText) {
            return;
        }

        const widthOfMenuelement: number = element.offsetWidth;

        const config = this.overlayService.getConnectedToConfig<string>(element, '.dd-badge-additional-info', this.infoText);

        if (config.overlayConfig) {
            config.overlayConfig.minWidth = widthOfMenuelement;
            config.closeOnOutsidePointerEvents = true;
        }

        this.overlayModel = this.overlayService.open(BadgeInfoOverlayComponent, config);

        this.overlayModel.overlayRef
            .detachments()
            .pipe(
                // Add delay so when clicking anchor (that is outside event) the anchor click happens first and doesnt open a new overlay.
                delay(1),
                take(1),
                takeUntil(this._unsubscribeAllSubject)
            )
            .subscribe(() => {
                this.closeMenu();
            });
    }

    /**
     * Closes the tooltip and cleans up the overlay model.
     */
    private closeMenu(): void {
        this.overlayModel?.close();
        delete this.overlayModel;
    }
}

