import { ChangeDetectionStrategy, Component, EventEmitter, HostBinding, Input, Output } from '@angular/core';

export type ButtonSize = 'large' | 'normal' | 'mini';
export type ButtonColor = 'primary-light' | 'primary-dark' | 'secondary' | 'green' | 'gray' | 'yellow' | 'red';

/**
 * A versatile button component with various customization options.
 */
@Component({
    selector: 'dd-button',
    templateUrl: './button.component.html',
    styleUrls: ['./button.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ButtonComponent {
    /**
     * The text to be displayed on the button.
     */
    @Input() text?: string;

    /**
     * If true, the button is disabled and does not emit click events.
     */
    @Input() disabled: boolean = false;

    /**
     * The name of the icon to be displayed on the button.
     */
    @Input() icon?: string;

    /**
     * The color variant of the button.
     */
    @Input() color: ButtonColor = 'secondary';

    /**
     * A custom background color for the button, specified as a hex code.
     */
    @Input() backgroundHex?: string;

    /**
     * The size of the button.
     */
    @Input() size: ButtonSize = 'normal';

    /**
     * If true, the button will use full width styles and take up all available space.
     */
    @Input() @HostBinding('class.full-width') fullWidth: boolean = false;

    /**
     * An aria label for the button, for accessibility purposes.
     */
    @Input() ariaLabel?: string;

    /**
     * A URL or array of URL segments to be used as the link for the button.
     */
    @Input() url?: string | string[];

    /**
     * If set, the URL will be opened in a new tab.
     */
    @Input() target?: '_blank';

    /**
     * The type of the button.
     * 'submit' should be used for buttons inside forms.
     */
    @Input() buttonType?: 'button' | 'submit' = 'button';

    /**
     * An event that is emitted when the button is clicked.
     */
    @Output() buttonClick: EventEmitter<void> = new EventEmitter<void>();

    /**
     * Emits the buttonClick event if the button is not disabled.
     */
    onButtonClick(): void {
        if (this.disabled) {
            return;
        }

        this.buttonClick.emit();
    }

    /**
     * Returns an object that maps class names to boolean values for styling the button.
     */
    get buttonClasses(): { [klass: string]: boolean } {
        return {
            'dd-btn-icon': !!this.icon,
            'btn-disabled': this.disabled,
            large: this.size === 'large',
            mini: this.size === 'mini',
            'custom-hover': !!this.backgroundHex,
            'dd-white-fg': !!this.backgroundHex && this.size === 'mini',
            [this.color]: !this.backgroundHex,
        };
    }

    /**
     * Returns an object that maps class names to boolean values for styling the button's icon.
     */
    get buttonIconClasses(): { [klass: string]: boolean } {
        return {
            'dd-accent-fg': (this.color === 'primary-dark' || this.color === 'gray') && !this.disabled,
            'dd-white-fg': this.size === 'mini' && !!this.backgroundHex && !this.disabled,
            'dd-primary-500-fg': this.color === 'primary-light' && !this.disabled,
            'ml-spacer-xmini': this.size === 'normal',
            'ml-spacer-mini': this.size === 'large',
            'ml-spacer-solo': this.size === 'mini',
        };
    }
}

