@use '../../../../styles/themes/themify';

@mixin create-card-styles($variant) {
    &.#{$variant} {
        @include themify.dd-themify() {
            $background: themify.dd-configuration('card', $variant, 'background');
            $color: themify.dd-configuration('card', $variant, 'foreground');

            @if $background != null {
                background-color: $background;
            }

            @if $color != null {
                color: $color;
            }

            $cardSpacer-x: themify.dd-configuration('card', $variant, 'spacer-x');
            $cardSpacer-y: themify.dd-configuration('card', $variant, 'spacer-y');

            $spacerDefault-x: themify.dd-configuration('spacers', 'default', $cardSpacer-x);
            $spacerMobile-x: themify.dd-configuration('spacers', 'mobile', $cardSpacer-x);

            $spacerDefault-y: themify.dd-configuration('spacers', 'default', $cardSpacer-y);
            $spacerMobile-y: themify.dd-configuration('spacers', 'mobile', $cardSpacer-y);

            @if $spacerDefault-x != null {
                padding-left: #{$spacerDefault-x}px !important;
                padding-right: #{$spacerDefault-x}px !important;

                @include breakpoints.media-breakpoint('lt-md') {
                    padding-left: #{$spacerMobile-x}px !important;
                    padding-right: #{$spacerMobile-x}px !important;
                }
            }

            @if $spacerDefault-y != null {
                padding-top: #{$spacerDefault-y}px !important;
                padding-bottom: #{$spacerDefault-y}px !important;

                @include breakpoints.media-breakpoint('lt-md') {
                    padding-top: #{$spacerMobile-y}px !important;
                    padding-bottom: #{$spacerMobile-y}px !important;
                }
            }
        }
    }
}

.dd-card {
    @include create-card-styles('basic');
    @include create-card-styles('small');
}

