import { ChangeDetectionStrategy, Component, EventEmitter, Input, Output } from '@angular/core';

/**
 * Supported variants for chip.
 */
export type ChipVariant = 'removable' | 'tag';

/**
 * Supported colors for chips.
 */
export type ChipIconColor = 'dd-danger-fg' | 'dd-accent-fg';

/**
 * Component that provides a customizable chip element.
 * It can be used in various contexts.
 */
@Component({
    selector: 'dd-chip',
    templateUrl: './chip.component.html',
    styleUrls: ['./chip.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ChipComponent {
    /**
     * The text to be displayed on the chip.
     */
    @Input() chipText?: string;

    /**
     * Determines whether the chip is selected.
     * A selected chip has a accent color.
     */
    @Input() selected: boolean = false;

    /**
     * The variant of the chip.
     * 'removable' variant has a white background and red hover.
     * 'tag' variant changes hover to white button and secondary button when selected.
     */
    @Input() chipVariant: ChipVariant = 'removable';

    /**
     * The icon to be displayed on the chip.
     * If undefined, no icon is rendered.
     */
    @Input() icon?: string;

    /**
     * The color of the icon on the chip.
     */
    @Input() iconColor: ChipIconColor = 'dd-accent-fg';

    /**
     * The number of notifications to be displayed on the chip.
     * If defined, the icon will not be shown.
     */
    @Input() notifications?: number;

    /**
     * Determines whether the propagation of the click event should be stopped.
     * When true, event.stopPropagation() is not called.
     * This is useful when used in tab navigation, as stopping the event from propagating can interfere with routing.
     */
    @Input() stopPropagation: boolean = false;

    /**
     * Emits an event when the chip is clicked.
     */
    @Output() chipClick: EventEmitter<void> = new EventEmitter();

    /**
     * Handles the click event on the chip.
     * If stopPropagation is true, it stops the event from bubbling up.
     */
    onChipClick(e: Event): void {
        if (this.stopPropagation) {
            e.stopPropagation();
        }

        this.chipClick.emit();
    }

    /**
     * Helper method for rendering ngClass for the chip.
     * It returns an object where the keys are the class names and the values are boolean expressions determining whether the class should be applied.
     */
    get chipClasses(): { [klass: string]: boolean } {
        return {
            'dd-accent dd-accent-border': this.selected,
            'dd-white': !this.selected,
            'dd-accent-darker-hover dd-accent-darkest-active': this.chipVariant === 'tag' && this.selected,
            'dd-primary-100-hover dd-primary-50-active': this.chipVariant === 'tag' && !this.selected,
            removable: this.chipVariant === 'removable',
            'pl-spacer-mini pr-spacer-xmini': !!this.icon,
            'px-spacer-mini': !this.icon,
        };
    }

    /**
     * Helper method for rendering ngClass for the chip notifications.
     * It returns an object where the keys are the class names and the values are boolean expressions determining whether the class should be applied.
     */
    get notificationClasses(): { [klass: string]: boolean } {
        return {
            'dd-white': this.selected,
            'dd-primary-300': !this.selected,
            'px-spacer-solo': true,
            'px-spacer-xmini': this.notifications !== undefined && this.notifications > 9,
        };
    }
}

