@use '../../../../styles/themes/themify';
@use '../../../../styles/helpers/hover';

.calendar-day {
    position: relative;
    &.today {
        span {
            text-decoration: underline solid 2px;
            text-underline-offset: 3px;
        }
    }

    &.not-this-month {
        @include themify.dd-themify() {
            background-color: themify.dd-color('white') !important;
            color: themify.dd-color('primary-200') !important;
        }
    }

    &.not-selectable {
        @include themify.dd-themify() {
            background-color: themify.dd-color('white') !important;
            color: themify.dd-color('primary-400') !important;
        }
    }

    &.selectable {
        cursor: pointer;
        @include themify.dd-themify() {
            background-color: themify.dd-color('primary-100') !important;
            color: themify.dd-color-contrast('primary-100') !important;
        }

        &:hover {
            @include themify.dd-themify() {
                background-color: themify.dd-color('primary-200') !important;
            }
        }

        @include hover.hover-transition();
    }

    &.highlighted {
        cursor: pointer;
        @include themify.dd-themify() {
            background-color: themify.dd-color('success') !important;
            color: themify.dd-color-contrast('success') !important;
        }

        &:hover {
            @include themify.dd-themify() {
                background-color: themify.dd-color('success-darker') !important;
            }
        }

        @include hover.hover-transition();
    }

    &.no-slots-highlighted {
        cursor: pointer;
        @include themify.dd-themify() {
            background-color: themify.dd-color('yellow') !important;
            color: themify.dd-color-contrast('yellow') !important;
        }

        &:hover {
            @include themify.dd-themify() {
                background-color: themify.dd-color('yellow') !important;
                filter: brightness(80%);
            }
        }

        @include hover.hover-transition();
    }

    &.selected {
        @include themify.dd-themify() {
            background-color: themify.dd-color('accent') !important;
            color: themify.dd-color-contrast('accent') !important;
            font-weight: bold;
        }

        &:hover {
            @include themify.dd-themify() {
                background-color: themify.dd-color('accent-darker') !important;
            }
        }

        // If selected then cross should have selected accent color
        .cross {
            @include themify.dd-themify() {
                background-color: themify.dd-color-contrast('accent');
            }
        }

        @include hover.hover-transition();
    }

    &.selected-border {
        border: 2px solid black;
    }

    // Cross is only shown if with no-slots-highlighted class so use yellow accent
    .cross {
        width: 100%;
        height: 2px;
        position: absolute;
        rotate: -45deg;

        @include themify.dd-themify() {
            background-color: themify.dd-color-contrast('yellow');
        }
    }
}

