import { Component, HostBinding, Input } from '@angular/core';
import { OverlayAnimationState, overlayAnimations } from '../../models/animations';

/**
 * Wrapper component for dialog. This component provides the overlay with a slideFromRight
 * animation and the classes used for the dialog panel. For an overlay wrapper
 * with a in-top animation, use OverlayWrapperComponent.
 */
@Component({
    selector: 'dd-dialog-wrapper',
    template: '<ng-content></ng-content>',
    animations: [overlayAnimations.slideFromRight],
})
export class DialogWrapperComponent {
    @Input() viewState?: OverlayAnimationState;

    @HostBinding('class') klass = 'dd-white-bg elevation-black h-100-p';

    // Keeping correct layout for the overlay.
    @HostBinding('style.display') style_display = 'flex';
    @HostBinding('style.flexDirection') get style_flexDirection() {
        return 'column';
    }

    // If no viewstate is provided, give a default state to keep position.
    @HostBinding('@slideFromRight') get slideFromRightState(): OverlayAnimationState | undefined {
        if (this.viewState) {
            return this.viewState;
        } else {
            return 'default';
        }
    }

    // When no animation is used, disable trigger.
    @HostBinding('@.disabled') get hasAnimation(): boolean {
        if (!this.viewState) {
            return true;
        } else {
            return false;
        }
    }
}

