/**
 * Provides a divider component that can be customized with different colors and margins.
 */
@Component({
    selector: 'dd-divider',
    template: '',
    styleUrls: ['./divider.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class DividerComponent {
    /**
     * The name of the spacer to use for adding a negative margin to the divider.
     * The parent component should have added a spacer with this name.
     */
    @Input() spacerName?: string;

    /**
     * The color variant to use for the divider.
     * This component supports all color variants.
     */
    @Input() colorVariant: string = 'dd-primary-200';

    /**
     * Dynamically generates the class attribute for the host element.
     * This ensures the divider is displayed correctly based on the provided spacer and color variant.
     */
    @HostBinding('class') get class(): string {
        let defaultClasses: string = 'mt-spacer-mini pb-spacer-mini border-top';
        if (this.spacerName) {
            defaultClasses += ` minus-mx-${this.spacerName}`;
        }
        defaultClasses += ` ${this.colorVariant}-border`;

        return defaultClasses;
    }
}

