import { Component, EventEmitter, forwardRef, Input, OnChanges, Output, SimpleChanges } from '@angular/core';
import { ControlValueAccessor, FormControl, NG_VALIDATORS, NG_VALUE_ACCESSOR, ValidationErrors } from '@angular/forms';
import { FileErrorMessages } from './../../file-validator';

/**
 * This component provides a file upload functionality with validation and error handling.
 * It supports multiple file inputs and allows customization of the upload text, disclaimer text, and label.
 * It also provides a variant for different sizes and a customizable background color.
 */
@Component({
    selector: 'dd-file-upload',
    templateUrl: './file-upload.component.html',
    styleUrls: ['./file-upload.component.scss'],
    providers: [
        {
            provide: NG_VALUE_ACCESSOR,
            useExisting: forwardRef(() => FileUploadComponent),
            multi: true,
        },
        {
            provide: NG_VALIDATORS,
            useExisting: forwardRef(() => FileUploadComponent),
            multi: true,
        },
    ],
})
export class FileUploadComponent implements ControlValueAccessor, OnChanges {
    /** Text to display in the file upload area. */
    @Input() text: string = 'Drop file or click to select';

    /** Optional disclaimer text to display below the file upload area. */
    @Input() disclaimerText?: string;

    /** Optional label to display above the file upload area. */
    @Input() label?: string = '';

    /** Determines whether the component should accept multiple file inputs. */
    @Input() multiple: boolean = true;

    /** Custom error messages for file validation. */
    @Input() errorMessages?: FileErrorMessages;

    /** Validation errors from the form control. */
    @Input() errors?: ValidationErrors | null;

    /** Indicates whether the form has been submitted. */
    @Input() submitted?: boolean;

    /**
     * Defines the default size of the file upload.
     * 'small' is the same size as form input and 'medium' has a minimum height of 200px.
     */
    @Input() variant: `small` | 'medium' = 'small';

    /**
     * Allows changing the background color of the input if the element is used on a different background color than white.
     * Use color classes that define both background and font color from the library.
     */
    @Input() backgroundColor: string = 'dd-white';

    /** Emits an event when the file input value changes. */
    @Output() private valueChanged = new EventEmitter<File[]>();

    /** Array of files selected for upload. */
    files: File[] = [];

    /** Form control for the file input. */
    value: FormControl<File[] | null | undefined> = new FormControl();

    /** Determines whether errors should be displayed. */
    showErrors: boolean = false;

    /** Default error message for invalid files. */
    private readonly genericError: string = 'Invalid file';

    /** Utility function to get keys from an object. */
    // eslint-disable-next-line @typescript-eslint/naming-convention
    ObjectKeys: <T>(o: T) => Extract<keyof T, string>[] = Object.keys as <T>(o: T) => Extract<keyof T, string>[];

    /**
     * Returns the appropriate error message for the current validation errors.
     * If a custom error message is provided for the current error, it will be used.
     * Otherwise, a generic error message will be returned.
     */
    get errorMessage(): string | undefined {
        if (this.value.errors) {
            const keys: string[] = this.ObjectKeys(this.value.errors);

            if (this.errorMessages) {
                const key: keyof FileErrorMessages | undefined = this.ObjectKeys(this.errorMessages).find((errorKey) => keys.includes(errorKey));

                // Return error message if the key is found.
                if (key && this.errorMessages[key]) {
                    return this.errorMessages[key];
                }
            }

            return this.genericError;
        }

        return undefined;
    }

    /** Returns the appropriate classes for the file upload container based on the variant. */
    get containerClass(): { [klass: string]: boolean } {
        return {
            ['default-size']: this.variant === 'small',
            ['min-h-200']: this.variant === 'medium',
        };
    }

    /**
     * Handles the file select event.
     * Converts the FileList to an array, checks for multiple files if necessary, and clears the input value.
     */
    onSelectFiles(event: Event): void {
        const fileArray = this.convertToArray((event.target as HTMLInputElement).files);
        this.checkForMultiple(fileArray);
        /**
         * Clear input for file select to work the same way as file drop,
         * and allow multiple files with the same name. Validator will inform about this
         * if it is not allowed.
         */
        (event.target as HTMLInputElement).value = '';
    }

    ngOnChanges(changes: SimpleChanges) {
        if (changes.errors) {
            setTimeout(() => {
                this.value.setErrors(changes.errors.currentValue);
            });
        }

        if (this.submitted) {
            this.showErrors = true;
        }
    }

    onFileDropped(files: File[]) {
        const fileArray = this.convertToArray(files);
        this.checkForMultiple(fileArray);
    }

    writeValue(files: File[]): void {
        const fileArray = this.convertToArray(files);
        if (fileArray.length < 2 || this.multiple) {
            this.files = fileArray;
            this.value.setValue(files);
            this.emitChanges(this.files);
        } else {
            throw Error('Multiple files not allowed');
        }
    }

    private checkForMultiple(fileArray: File[]): void {
        if (this.multiple) {
            const merged = this.files.concat(fileArray);
            this.writeValue(merged);
        } else {
            this.writeValue(fileArray);
        }
    }

    private convertToArray(files: FileList | File[] | File | null | undefined): File[] {
        if (files) {
            if (files instanceof File) {
                return [files];
            } else if (Array.isArray(files)) {
                return files;
            } else {
                return Array.prototype.slice.call(files);
            }
        }

        return [];
    }

    removeFile(file: File) {
        const fileIndex = this.files.indexOf(file);

        if (fileIndex >= 0) {
            const currentFiles = this.files.slice();
            currentFiles.splice(fileIndex, 1);
            this.writeValue(currentFiles);
        }
    }

    private emitChanges(files: File[]) {
        this.valueChanged.emit(files);
        this._onChange(files);
    }

    registerOnChange(fn: any): void {
        this._onChange = fn;
    }

    /**
     * Register callback function as an internal method.
     */
    registerOnTouched(fn: () => void): void {
        this._onTouched = fn;
    }

    registerOnValidatorChange(fn: () => void): void {
        this._onChange = fn;
    }

    /**
     * Returns form validation errors or null if form is valid.
     */
    validate(): ValidationErrors | null {
        if (!this.value.valid && this.errors) {
            return this.errors;
        }

        // tslint:disable-next-line: no-null-keyword
        return null;
    }

    private _onChange = (val: File[]) => {};
    private _onTouched: () => void = () => {};
}

