import { AfterViewInit, ChangeDetectorRef, Component, forwardRef, Input, OnChanges, OnDestroy, SimpleChanges } from '@angular/core';
import { ControlValueAccessor, FormControl, NG_VALUE_ACCESSOR, ValidationErrors } from '@angular/forms';
import { Subject } from 'rxjs';
import { ValidationErrorMessages } from '../../form-field-models';

/**
 * This component provides a checkbox input field with validation and error handling.
 * It supports form submission and initialization validation.
 */
@Component({
    selector: 'dd-form-checkbox',
    templateUrl: './form-checkbox.component.html',
    styleUrls: ['./form-checkbox.component.scss'],
    providers: [
        {
            provide: NG_VALUE_ACCESSOR,
            useExisting: forwardRef(() => FormCheckboxComponent),
            multi: true,
        },
    ],
})
export class FormCheckboxComponent implements ControlValueAccessor, OnChanges, OnDestroy, AfterViewInit {
    constructor(private cdr: ChangeDetectorRef) {}

    /** Label for the checkbox. */
    @Input() label?: string;

    /**
     * Determines whether the checkbox should be validated when the form is submitted.
     * If not provided, the component will mark itself as touched whenever the value is changed.
     */
    @Input() submitted?: boolean;

    /** Determines whether the checkbox is disabled. */
    @Input() checkboxDisabled: boolean = false;

    /** Custom error messages for checkbox validation. */
    @Input() errorMessages?: ValidationErrorMessages;

    /** Validation errors from the form control. */
    @Input() errors?: ValidationErrors | null;

    /** Determines whether the checkbox should be validated on initialization. */
    @Input() initValidation: boolean = true;

    /** Form control for the checkbox input. */
    value: FormControl<boolean | undefined | null> = new FormControl();

    /** Determines whether the checkbox is checked. */
    checked: boolean = false;

    /** Default error message for invalid checkbox. */
    private readonly genericError: string = 'Checkbox required';

    /** Stores the submitted value for the checkbox. */
    private _submitted: boolean = false;

    /** Subject to emit when the component is destroyed. */
    private destroySubject: Subject<void> = new Subject();

    /**
     * Utility function to get keys from an object.
     * https://github.com/microsoft/TypeScript/pull/12253#issuecomment-393954723
     */
    // eslint-disable-next-line @typescript-eslint/naming-convention
    ObjectKeys: <T>(o: T) => Extract<keyof T, string>[] = Object.keys as <T>(o: T) => Extract<keyof T, string>[];

    /**
     * Finds the error message from the input.
     *
     * @returns The error message if found, otherwise the generic error message.
     */
    get errorMessage(): string | undefined {
        if (this.value.errors) {
            const keys: string[] = this.ObjectKeys(this.value.errors);

            if (this.errorMessages) {
                const key: keyof ValidationErrorMessages | undefined = this.ObjectKeys(this.errorMessages).find((errorKey) => keys.includes(errorKey));

                // Return error message if the key is found.
                if (key && this.errorMessages[key]) {
                    return this.errorMessages[key];
                }
            }
            return this.genericError;
        }

        return undefined;
    }

    /**
     * Handles the change event for the checkbox.
     * Toggles the checked state and propagates the change.
     */
    handleChangeEvent(): void {
        this.checked = !this.checked;
        this.propagateChange(this.checked);
    }

    /**
     * Runs one change detection and marks the form field as touched.
     * This allows the error to be shown if the form field is initialized with an invalid value.
     */
    ngAfterViewInit(): void {
        // If there are errors then trigger new change detection cycle which marks the field as touched.
        if (this.errors && this.initValidation && this.value?.value) {
            setTimeout(() => {
                this.markAsTouched();
                this.value.updateValueAndValidity();
                this.cdr.detectChanges();
            });
        }
    }

    ngOnChanges(changes: SimpleChanges): void {
        // If the form is invalid then set the error message as custom error.
        // Timeout required to show errors at init https://github.com/angular/angular/issues/19170#issuecomment-341555371
        if (changes.errors) {
            setTimeout(() => {
                this.value.setErrors(changes.errors.currentValue);
            });
        }

        if (this.submitted !== undefined) {
            // Check if we should trigger detection cycle for the checkbox.
            if (this.submitted && !this._submitted) {
                this._submitted = this.submitted;
                this.markAsTouched();
            }
        }
    }

    ngOnDestroy(): void {
        this.destroySubject.next();
        this.destroySubject.complete();
    }

    /**
     * Write value from form model into the view.
     */
    writeValue(value: boolean): void {
        if (value !== undefined) {
            this.checked = value;
        }
    }

    /**
     * Register callback function as an internal method.
     */
    registerOnChange(fn: (_: any) => {}): void {
        this.propagateChange = fn;
    }

    /**
     * Register callback function as an internal method.
     */
    registerOnTouched(fn: () => void): void {
        this.onTouched = fn;
    }

    registerOnValidatorChange(fn: () => void): void {
        this._onChange = fn;
    }

    setDisabledState(disabled: boolean) {
        this.checkboxDisabled = disabled;
    }

    /** Marks the form control as touched. */
    markAsTouched() {
        this.onTouched();
        this.value.markAsTouched();
        this.cdr.detectChanges();
    }

    /**
     * Returns form validation errors or null if form is valid.
     */
    validate(): ValidationErrors | null {
        if (!this.value.valid && this.errors) {
            return this.errors;
        }

        // tslint:disable-next-line: no-null-keyword
        return null;
    }

    _onChange: () => void = () => {};

    onTouched: () => void = () => {};

    propagateChange = (_: any) => {};
}

