import { AfterViewInit, ChangeDetectorRef, Component, EventEmitter, forwardRef, Input, OnChanges, OnDestroy, OnInit, Output, SimpleChanges } from '@angular/core';
import { FormControl, NG_VALIDATORS, NG_VALUE_ACCESSOR, ValidationErrors } from '@angular/forms';
import { DatepickerOptions, defaultOptions } from '@digitaldelightsoy/design-library/src/datepicker';
import { ValidationErrorMessages } from '@digitaldelightsoy/design-library/src/form-field';
import { OverlayOpenModel, OverlayService } from '@digitaldelightsoy/design-library/src/overlay';
import { DdLocaleService } from '@digitaldelightsoy/design-library/src/pipes';
import { SelectItem } from '@digitaldelightsoy/design-library/src/select';
import { addMinutes } from 'date-fns';
import { getDate, getTime } from 'date-fns/esm';
import { delay, Subject, take, takeUntil } from 'rxjs';
import { FormFieldDatepickerComponent, FormFieldDatepickerModel } from '../form-field-datepicker/form-field-datepicker.component';

/**
 * This component provides a datepicker field with validation and error handling.
 * It supports form submission and initialization validation.
 * It also provides a variant for different sizes and a customizable background color.
 */
@Component({
    selector: 'dd-form-datepicker-field',
    templateUrl: './form-datepicker-field.component.html',
    styleUrls: ['./form-datepicker-field.component.scss'],
    providers: [
        {
            provide: NG_VALUE_ACCESSOR,
            useExisting: forwardRef(() => FormDatepickerFieldComponent),
            multi: true,
        },
        {
            provide: NG_VALIDATORS,
            useExisting: forwardRef(() => FormDatepickerFieldComponent),
            multi: true,
        },
    ],
})
export class FormDatepickerFieldComponent implements OnInit, OnChanges, OnDestroy, AfterViewInit {
    constructor(private overlayService: OverlayService, private cdr: ChangeDetectorRef, localeService: DdLocaleService) {
        /** Fetch the language locale data. */
        localeService
            .getDateLocale$()
            .pipe(takeUntil(this._unsubscribeAllSubject))
            .subscribe((locale?: Locale) => {
                this.locale = locale;
            });
    }

    /** Label for datepicker field. */
    @Input() dateLabel?: string;

    /**
     * Determines whether the timepicker should also be shown.
     * If not shown, datepicker will fill the whole row.
     */
    @Input() showTimePicker?: boolean = true;

    /** Label for time select field. */
    @Input() timeLabel?: string;

    /** Currently selected date. */
    @Input() selectedDate?: Date;

    /** Minimum hour selectable. */
    @Input() min: number = 0;

    /** Maximum hour selectable. */
    @Input() max: number = 24;

    /** Interval for the timepicker. */
    @Input() interval: number = 30;

    /**
     * Configuration for the datepicker.
     * By default, all dates are selectable.
     */
    @Input() options: DatepickerOptions = {
        ...defaultOptions,
        allSelectable: true,
    };

    /** Validation errors from the form control. */
    @Input() errors?: ValidationErrors | null;

    /** Custom error messages for datepicker validation. */
    @Input() errorMessages?: ValidationErrorMessages;

    /** Determines whether the datepicker should be validated when the form is submitted. */
    @Input() submitted?: boolean = false;

    /** Text to be shown when there is no selected item. */
    @Input() selectDateText: string = 'Select Date';

    /**
     * If you are using the element on a different background color than white,
     * you can change the background color of the input.
     * Use color classes that define both background and font color from the library.
     * Default is white background, with white some fonts are using lighter gray color than the contrast is.
     */
    @Input() backgroundColor: string = 'dd-white';

    /** Output event that is emitted when an item is clicked in datepicker. */
    @Output() itemClick: EventEmitter<SelectItem<any>> = new EventEmitter();

    /** Form control for the datepicker input. */
    value: FormControl<Date | null | undefined> = new FormControl();

    /** Locale data for the datepicker. */
    locale?: Locale;

    /** String representation of the selected time. */
    selectedTimeString?: string;

    /** Selected time value. */
    selectedTimeValue?: [number, number];

    /** List of selectable times. */
    selectableTimes: SelectItem<[number, number]>[] = [];

    overlayModel?: OverlayOpenModel<FormFieldDatepickerComponent>;

    private destroySubject: Subject<void> = new Subject();

    /** Generic error message. */
    private readonly genericError: string = 'Invalid field';

    private readonly _unsubscribeAllSubject: Subject<void> = new Subject();

    /**
     * Returns the keys from the Object.keys as keyof T.
     *
     * https://github.com/microsoft/TypeScript/pull/12253#issuecomment-393954723
     */
    // eslint-disable-next-line @typescript-eslint/naming-convention
    objectKeys: <T>(o: T) => Extract<keyof T, string>[] = Object.keys as <T>(o: T) => Extract<keyof T, string>[];

    /**
     * Find the error message from the input.
     */
    get errorMessage(): string | undefined {
        if (this.errors) {
            const keys: string[] = this.objectKeys(this.errors);

            if (this.errorMessages) {
                const key: keyof ValidationErrorMessages | undefined = this.objectKeys(this.errorMessages).find((errorKey) => keys.includes(errorKey));

                // Return error message if the key is found.
                if (key && this.errorMessages[key]) {
                    return this.errorMessages[key];
                }
            }

            return this.genericError;
        }

        return undefined;
    }

    ngOnInit(): void {
        // Loop through timeSlots to create list of selectItems
        const today = new Date();
        const minDate = new Date(today.getFullYear(), today.getMonth(), today.getDate(), this.min, 0, 0);
        const maxDate = new Date(today.getFullYear(), today.getMonth(), today.getDate(), this.max, 0, 0);

        for (let i = minDate; i <= maxDate; ) {
            if (this.locale?.code) {
                const timeString = i.toLocaleTimeString([this.locale.code], { hour: '2-digit', minute: '2-digit' });
                this.selectableTimes.push({ id: getTime(i), text: timeString, value: [i.getHours(), i.getMinutes()] });
            }

            i = addMinutes(i, this.interval);
        }
    }

    ngAfterViewInit(): void {
        setTimeout(() => {
            if (this.selectableTimes?.length && this.selectedDate) {
                // Set initial selected times.
                // Pick closest time to provided Date in selectableTimes list.

                const compareTimeValue = [this.selectedDate?.getHours(), this.selectedDate?.getMinutes()];

                const tempTimeArr = this.selectableTimes.map((time) => {
                    if (time.value) {
                        const a = new Date().setUTCHours(compareTimeValue[0] as number, compareTimeValue[1]);
                        const b = new Date().setUTCHours(time.value[0] as number, time.value[1]);
                        return Math.abs(a - b);
                    } else {
                        return;
                    }
                });

                const index = tempTimeArr.indexOf(Math.min(...(tempTimeArr as number[])));

                this.selectedTimeValue = this.selectableTimes[index].value;
                this.selectedTimeString = this.selectableTimes[index].text as string;

                if (this.selectedTimeValue) {
                    this.setDateValue(this.selectedTimeValue);
                }
            } else {
                // If date isn't set, select initial time anyways. When form field is required, an error is shown if date field is empty.
                if (this.selectableTimes[0]) {
                    this.selectItemClick(this.selectableTimes[0]);
                }
            }
        }, 0);
    }

    private setDateValue(time: [number, number]): void {
        if (this.selectedDate) {
            const year = this.selectedDate?.getFullYear();
            const month = this.selectedDate?.getMonth();
            const day = this.selectedDate?.getDate();
            const hours = time[0];
            const minutes = time[1];

            this.selectedDate = new Date(year as number, month as number, day, hours, minutes);

            this.value.setValue(this.selectedDate);
        }
    }

    selectItemClick(time: SelectItem<[number, number]>): void {
        this.selectedTimeString = time.text as string;
        this.selectedTimeValue = time.value;

        if (time.value) {
            this.setDateValue(time.value);
        }
    }

    ngOnChanges(changes: SimpleChanges): void {
        if (changes.selectedDate && !changes.selectedDate.firstChange && this.selectedTimeValue) {
            const currentValue = changes.selectedDate.currentValue;
            const previousValue = changes.selectedDate.previousValue;

            // Only set timeslot value to previously selected if date is changed. If it is not set, the time will be 00:00 from the date provided as form value,
            if (this.selectableTimes[0].value && getDate(previousValue) !== getDate(currentValue)) {
                this.setDateValue(this.selectedTimeValue);
            }
        }
    }

    onAnchorClick(element: HTMLDivElement): void {
        // Close existing overlay
        if (!!this.overlayModel) {
            this.closeMenu();
            return;
        }

        const data: FormFieldDatepickerModel = {
            options: this.options,
            selectedDate: this.selectedDate || new Date(),
        };

        const config = this.overlayService.getConnectedToConfig<FormFieldDatepickerModel>(element, '.dd-datepicker-field', data);

        if (config.overlayConfig) {
            config.closeOnOutsidePointerEvents = true;
        }

        this.overlayModel = this.overlayService.open(FormFieldDatepickerComponent, config);

        /** Close dialog when date is selected. */
        this.overlayModel.component.itemClick.pipe(take(1), takeUntil(this._unsubscribeAllSubject)).subscribe((date: Date) => {
            this.selectedDate = date;

            this.closeMenu();
        });

        this.overlayModel.overlayRef
            .detachments()
            .pipe(
                // Add delay so when clicking anchor (that is outside event) the anchor click happens first and doesnt open a new overlay.
                delay(1),
                take(1),
                takeUntil(this._unsubscribeAllSubject)
            )
            .subscribe(() => {
                this.closeMenu();
            });
    }

    /** Helper function to get rid of things. */
    private closeMenu(): void {
        this.value.setValue(this.selectedDate);

        this.overlayModel?.close();
        delete this.overlayModel;
    }

    /** Marks the form control as touched. */
    markAsTouched() {
        this.onTouched();
        this.value.markAsTouched();
        this.cdr.detectChanges();
    }

    ngOnDestroy(): void {
        this.destroySubject.next();
        this.destroySubject.complete();
    }

    writeValue(value?: Date): void {
        if (!value) {
            return;
        }

        this.value.setValue(value);
    }

    _onChange: () => void = () => {};
    onTouched: () => void = () => {};

    /**
     * Register callback function as an internal method.
     */
    registerOnChange(fn: () => void): void {
        this.value.valueChanges.pipe(takeUntil(this.destroySubject)).subscribe(fn);
    }

    /**
     * Register callback function as an internal method.
     */
    registerOnTouched(fn: () => void): void {
        this.onTouched = fn;
    }

    registerOnValidatorChange(fn: () => void): void {
        this._onChange = fn;
    }

    /**
     * Returns form validation errors or null if form is valid.
     */
    validate(): ValidationErrors | null {
        if (!this.value.valid && this.errors) {
            return this.errors;
        }

        // tslint:disable-next-line: no-null-keyword
        return null;
    }
}

