import { AfterViewInit, ChangeDetectorRef, Component, ElementRef, forwardRef, Input, OnChanges, OnDestroy, SimpleChanges, ViewChild } from '@angular/core';
import { ControlValueAccessor, FormControl, NG_VALIDATORS, NG_VALUE_ACCESSOR, ValidationErrors, Validator } from '@angular/forms';
import { Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';
import { ValidationErrorMessages } from '../../form-field-models';

/**
 * This component provides a form field with validation and error handling.
 * It supports form submission and initialization validation.
 * It also provides a variant for different sizes and a customizable background color.
 */
@Component({
    selector: 'dd-form-field',
    templateUrl: './form-field.component.html',
    styleUrls: ['./form-field.component.scss'],
    providers: [
        {
            provide: NG_VALUE_ACCESSOR,
            useExisting: forwardRef(() => FormFieldComponent),
            multi: true,
        },
        {
            provide: NG_VALIDATORS,
            useExisting: forwardRef(() => FormFieldComponent),
            multi: true,
        },
    ],
})
export class FormFieldComponent implements ControlValueAccessor, Validator, OnChanges, AfterViewInit, OnDestroy {
    constructor(private cdr: ChangeDetectorRef) {}

    /** Label for the form field. */
    @Input() label?: string;

    /**
     * Name of the input field.
     */
    @Input() name?: string;

    /**
     * How the field should be autocompleted.
     * Refer to https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/autocomplete
     */
    @Input() autoComplete?: string;

    /**
     * Type of the input.
     * 'textarea' type converts the normal <input> to <textarea>.
     */
    @Input() type: 'text' | 'password' | 'textarea' | 'number' = 'text';

    /** Custom error messages. */
    @Input() errorMessages?: ValidationErrorMessages;

    /** Validation errors. */
    @Input() errors?: ValidationErrors | null;

    /** Determines if validation should be initialized. */
    @Input() initValidation: boolean = true;

    /**
     * Determines if the form field should be validated when the form is submitted.
     * If this input is not provided, the component will simply mark itself as touched whenever the value is changed.
     */
    @Input() submitted?: boolean;

    /** Determines if padding reserved for hint and error elements should be removed. */
    @Input() noPadding: boolean = false;

    /** Determines if the form field should be disabled. */
    @Input() fieldDisabled: boolean = false;

    /**
     * Determines if a loading indicator should be displayed inside the form field.
     */
    @Input() loading: boolean = false;

    /**
     * Sets the background color of the input.
     * If you are using the element on a different background color than white, you can change the background color of the input.
     * Use color classes that define both background and font color from the library.
     */
    @Input() backgroundColor: string = 'dd-white';

    /** Sets the height class for the textarea input. */
    @Input() textareaHeight: string = 'h-200';

    /** Determines if autofocus should be enabled. */
    @Input() autofocus: boolean = false;

    /**
     * Reference to the input field to be autofocused on.
     */
    @ViewChild('input') inputField?: ElementRef;

    /**
     * Sets the tab index of the form field.
     * Tab index is null on default so that the attribute is set only if explicitly stated.
     */
    @Input() tabIndex?: number | null;

    value: FormControl<string | null | undefined> = new FormControl();

    /** Generic error message. */
    private readonly genericError: string = 'Invalid field';

    /**
     * Save the submitted value to the component.
     */
    private _submitted: boolean = false;

    private destroySubject: Subject<void> = new Subject();

    /**
     * Returns the keys from the Object.keys as keyof T.
     *
     * https://github.com/microsoft/TypeScript/pull/12253#issuecomment-393954723
     */
    // eslint-disable-next-line @typescript-eslint/naming-convention
