@use '../../../../styles/themes/themify';
@use '../../../../styles/helpers/hover';

:host {
    width: 100%;
    min-width: 100%;
    max-width: 100%;

    .form-field-container {
        position: relative;

        label {
            position: absolute;
            left: 9px;
            z-index: 2;
            font-size: 12px;
            line-height: 11px;
            background-color: inherit;
            padding: 0 4px;
            // Make the label only the half of the actual height
            // Align content so text is rendered from the bottom
            // Move 1 px up to go over the border.
            height: 6px;
            top: -1px;
            display: flex;
            align-items: flex-end;
        }

        input,
        textarea {
            display: block;
            padding: 12px;
            // Add margin so all inputs reserve space for error-text
            margin-bottom: 16px;

            @include themify.dd-themify() {
                height: themify.dd-configuration('inputs', default-size);
            }

            &:not([disabled]) {
                &:hover {
                    @include themify.dd-themify() {
                        border-color: themify.dd-color('primary-600') !important;
                    }
                }

                @include hover.hover-transition();

                &:active,
                &:focus {
                    @include themify.dd-themify() {
                        border-color: themify.dd-color('primary-300') !important;
                    }
                }
            }

            // Remove number input default styling.
            /* Chrome, Safari, Edge, Opera */
            &::-webkit-outer-spin-button,
            &::-webkit-inner-spin-button {
                -webkit-appearance: none;
                margin: 0;
            }

            /* Firefox */
            &[type='number'] {
                -moz-appearance: textfield;
            }
        }

        .spinner {
            position: absolute;
            top: 15px;
            right: 10px;
        }

        // Minus margin so resizable textarea also reserves space for error-text
        .error-text {
            margin-top: -16px;
        }
    }
}

