import { AfterViewInit, ChangeDetectorRef, Component, forwardRef, Input, OnChanges, OnDestroy, SimpleChanges } from '@angular/core';
import { ControlValueAccessor, FormControl, NG_VALUE_ACCESSOR, ValidationErrors } from '@angular/forms';
import { Subject } from 'rxjs';

/**
 * This component provides a radio button within a form with validation and error handling.
 * It supports form submission and initialization validation.
 */
@Component({
    selector: 'dd-form-radio',
    templateUrl: './form-radio.component.html',
    styleUrls: ['./form-radio.component.scss'],
    providers: [
        {
            provide: NG_VALUE_ACCESSOR,
            useExisting: forwardRef(() => FormRadioComponent),
            multi: true,
        },
    ],
})
export class FormRadioComponent implements ControlValueAccessor, OnChanges, OnDestroy, AfterViewInit {
    constructor(private cdr: ChangeDetectorRef) {}

    /** Label for the radio button. */
    @Input() label?: string;

    /**
     * Determines if the form radio should be validated when the form is submitted.
     * If this input is not provided, the component will simply mark itself as touched whenever the value is changed.
     */
    @Input() submitted?: boolean;

    /** Determines if the radio button should be disabled. */
    @Input() radioDisabled: boolean = false;

    /** Validation errors. */
    @Input() errors?: ValidationErrors | null;

    /** Value of the radio button. */
    @Input() value: any = '';

    /** Name of the radio group. */
    @Input() name: string = '';

    /** FormControl instance for the radio button. */
    control: FormControl<string | undefined | null> = new FormControl();

    /** Indicates whether the radio button is checked. */
    checked: boolean = false;

    /**
     * Internal state to keep track of form submission.
     */
    private _submitted: boolean = false;

    private destroySubject: Subject<void> = new Subject();

    /**
     * Handles event change for the component.
     *
     */
    handleChangeEvent(): void {
        this.checked = true;
        this.onChange(this.value);
    }

    /**
     * Run one change detection and mark the form field as touched,
     * so the error is shown if the form field is initialized with invalid value.
     */
    ngAfterViewInit(): void {
        // If there are errors then trigger new change detection cycle which marks the field as touched.
        if (this.errors && this.value) {
            setTimeout(() => {
                this.markAsTouched();
                this.control.updateValueAndValidity();
                this.cdr.detectChanges();
            });
        }
    }

    ngOnChanges(changes: SimpleChanges): void {
        if (this.submitted !== undefined) {
            // Check if we should trigger detection cycle for the radio.
            if (this.submitted && !this._submitted) {
                this._submitted = this.submitted;
                this.markAsTouched();
            }
        }
    }

    ngOnDestroy(): void {
        this.destroySubject.next();
        this.destroySubject.complete();
    }

    /**
     * Write value from form model into the view.
     */
    writeValue(value: any): void {
        if (value !== undefined) {
            this.checked = value === this.value;
        }
    }

    registerOnValidatorChange(fn: () => void): void {
        this.onChange = fn;
    }

    setDisabledState(disabled: boolean) {
        this.radioDisabled = disabled;
    }

    /** Marks the form control as touched. */
    markAsTouched() {
        this.onTouch();
        this.control.markAsTouched();
        this.cdr.detectChanges();
    }

    /**
     * Returns form validation errors or null if form is valid.
     */
    validate(): ValidationErrors | null {
        if (!this.control.valid && this.errors) {
            return this.errors;
        }

        // tslint:disable-next-line: no-null-keyword
        return null;
    }

    onChange: any = () => {};
    onTouch: any = () => {};

    /**
     * Register callback function as an internal method.
     */
    registerOnChange(fn: any): void {
        this.onChange = fn;
    }

    /**
     * Register callback function as an internal method.
     */
    registerOnTouched(fn: any): void {
        this.onTouch = fn;
    }
}

