/**
 * This component provides a select input field within a form with validation and error handling.
 * It supports form submission and initialization validation.
 */
@Component({
    selector: 'dd-form-select',
    templateUrl: './form-select.component.html',
    styleUrls: ['./form-select.component.scss'],
    providers: [
        {
