            useExisting: forwardRef(() => FormSelectComponent),
            multi: true,
        },
        {
            provide: NG_VALIDATORS,
            useExisting: forwardRef(() => FormSelectComponent),
            multi: true,
        },
    ],
})
export class FormSelectComponent implements ControlValueAccessor, Validator, OnChanges, OnDestroy, AfterViewInit {
    constructor(private cdr: ChangeDetectorRef) {}

    /** If multiselect list should be used. */
    @Input() multi: boolean = false;

    /** Selectable items. */
    @Input() items: SelectItem<any>[] = [];

    /** Label to be shown on field. */
    @Input() label?: string;

    /** Error messages. */
    @Input() errorMessages?: ValidationErrorMessages;

    /* Component errors. */
    @Input() errors?: ValidationErrors | null;

    /* If validation should be run on init. */
    @Input() initValidation: boolean = true;

    /**
     * When form is submitted, form field is validated.
     */
    @Input() submitted?: boolean;

    /** The icon to be displayed. */
    @Input() icon: string = 'dd-icon-arrow-down';

    /** If search functionality is enabled. */
    @Input() searchEnabled: boolean = false;

    /**
     * If you are using the element on different background color than white, you can change the background color of the input.
     * Use color classes that define both background and font color from the library.
     * Default is white background, with white some fonts are using lighter gray color than the contrast is.
     */
    @Input() backgroundColor: string = 'dd-white';

    value: FormControl<SelectItem<string | number | undefined> | (string | number)[] | undefined> = new FormControl();

    multipleSelected?: (string | number)[];

    selectedId?: string | number;

    /**
     * Determine whether errors should be shown.
     */
    showErrors: boolean = false;

    /** Generic error message. */
    private readonly genericError: string = 'Invalid field';

    /**
     * Save the submitted value to the component.
     */
    private _submitted: boolean = false;

    private destroySubject: Subject<void> = new Subject();

    /**
     * Returns the keys from the Object.keys as keyof T.
     *
     * https://github.com/microsoft/TypeScript/pull/12253#issuecomment-393954723
     */
    // eslint-disable-next-line @typescript-eslint/naming-convention
    ObjectKeys: <T>(o: T) => Extract<keyof T, string>[] = Object.keys as <T>(o: T) => Extract<keyof T, string>[];

    /**
     * Find the error message from the input.
     */
    get errorMessage(): string | undefined {
        if (this.value.errors) {
            const keys: string[] = this.ObjectKeys(this.value.errors);

            if (this.errorMessages) {
                const key: keyof ValidationErrorMessages | undefined = this.ObjectKeys(this.errorMessages).find((errorKey) => keys.includes(errorKey));

                // Return error message if the key is found.
                if (key && this.errorMessages[key]) {
                    return this.errorMessages[key];
                }
            }

            return this.genericError;
        }

        return undefined;
    }

    /**
     * Run one change detection and mark the form field as touched,
     * so the error is shown if the form field is initialized with invalid value.
     */
    ngAfterViewInit(): void {
        // If there are errors then trigger new change detection cycle which marks the field as touched.
        if (this.errors && this.initValidation && this.value?.value) {
            setTimeout(() => {
                this.markAsTouched();
                this.value.updateValueAndValidity();
                this.cdr.detectChanges();
            });
        }
    }

    ngOnChanges(changes: SimpleChanges): void {
        // If the form is invalid then set the error message as custom error.
        // Timeout required to show errors at init https://github.com/angular/angular/issues/19170#issuecomment-341555371
        if (changes.errors) {
            setTimeout(() => {
                this.value.setErrors(changes.errors.currentValue);
            });
        }
        if (this.submitted !== undefined) {
            // Check if we should trigger detection cycle for the field.
            if (this.submitted && !this._submitted) {
                this._submitted = this.submitted;
                this.markAsTouched();
            }
        }
    }

    ngOnDestroy(): void {
        this.destroySubject.next();
        this.destroySubject.complete();
    }

    onSelectItem(item: SelectItem<string>): void {
        this.selectedId = item.id as string;
        this.value.setValue(item);
    }

    onSelectMultiItem([item, selections]: [SelectItem<any>, (string | number)[]]): void {
        this.multipleSelected = selections ?? [];
        if (selections.length > 0) {
            this.value.setValue(selections);
        } else {
            this.value.setValue(undefined);
        }
    }

    _onChange: () => void = () => {};

    writeValue(value: any): void {
        if (!value) {
            return;
        }

        if (this.multi) {
            this.multipleSelected = value.map((selected: SelectItem<any> | number | string) => (typeof selected === 'object' ? selected.id : selected)) as (string | number)[];
        } else {
            this.selectedId = value.id as string | number;
        }
        this.value.setValue(value);
    }

    onTouched: () => void = () => {};

    /**
     * Register callback function as an internal method.
     */
    registerOnChange(fn: () => void): void {
        this.value.valueChanges.pipe(takeUntil(this.destroySubject)).subscribe(fn);
    }

    /**
     * Register callback function as an internal method.
     */
    registerOnTouched(fn: () => void): void {
        this.onTouched = fn;
    }

    registerOnValidatorChange(fn: () => void): void {
        this._onChange = fn;
    }

    /** Marks the form control as touched. */
    markAsTouched() {
        this.onTouched();
        this.value.markAsTouched();
        this.cdr.detectChanges();
    }

    /**
     * Returns form validation errors or null if form is valid.
     */
    validate(): ValidationErrors | null {
        if (!this.value.valid && this.errors) {
            return this.errors;
        }

        // tslint:disable-next-line: no-null-keyword
        return null;
    }
}

