import { AfterViewInit, ChangeDetectorRef, Component, forwardRef, Input, OnChanges, OnDestroy, SimpleChanges } from '@angular/core';
import { ControlValueAccessor, FormControl, NG_VALUE_ACCESSOR, ValidationErrors } from '@angular/forms';
import { Subject } from 'rxjs';
import { ValidationErrorMessages } from '../../form-field-models';

/**
 * Component for a toggle slider input within a form with validation and error handling.
 * Supports form submission and initialization validation.
 */
@Component({
    selector: 'dd-form-toggle-slider',
    templateUrl: './form-toggle-slider.component.html',
    styleUrls: ['./form-toggle-slider.component.scss'],
    providers: [
        {
            provide: NG_VALUE_ACCESSOR,
            useExisting: forwardRef(() => FormToggleSliderComponent),
            multi: true,
        },
    ],
})
export class FormToggleSliderComponent implements ControlValueAccessor, OnChanges, OnDestroy, AfterViewInit {
    constructor(private cdr: ChangeDetectorRef) {}

    /** Label for the toggle slider input. */
    @Input() label?: string;

    /**
     * When form is submitted, form toggle slider is validated and new detection cycle is triggered.
     * If this input is not provided, the component will simply mark itself as touched whenever the value is changed.
     */
    @Input() submitted?: boolean;

    /** Indicates whether the toggle slider is disabled. */
    @Input() disabled: boolean = false;

    /** Validation errors for the toggle slider. */
    @Input() errors?: ValidationErrors | null;

    /** Indicates whether to perform initial validation. */
    @Input() initValidation: boolean = true;

    /** Error messages for the toggle slider. */
    @Input() errorMessages?: ValidationErrorMessages;

    /**  Form control for the toggle slider value. */
    value: FormControl<boolean | null | undefined> = new FormControl();

    /**  Indicates whether the toggle slider is checked. */
    checked: boolean = false;

    /** Generic error message for the toggle slider. */
    private readonly genericError: string = 'Slider Required';

    /**
     * Save the submitted value to the component.
     */
    private _submitted: boolean = false;

    private destroySubject: Subject<void> = new Subject();

    /**
     * Returns the keys from the Object.keys as keyof T.
     *
     * https://github.com/microsoft/TypeScript/pull/12253#issuecomment-393954723
     */
    ObjectKeys: <T>(o: T) => Extract<keyof T, string>[] = Object.keys as <T>(o: T) => Extract<keyof T, string>[];

    /**
     * Find the error message from the input.
     */
    get errorMessage(): string | undefined {
        if (this.value.errors) {
            const keys: string[] = this.ObjectKeys(this.value.errors);

            if (this.errorMessages) {
                const key: keyof ValidationErrorMessages | undefined = this.ObjectKeys(this.errorMessages).find((errorKey) => keys.includes(errorKey));

                // Return error message if the key is found.
                if (key && this.errorMessages[key]) {
                    return this.errorMessages[key];
                }
            }
            return this.genericError;
        }

        return undefined;
    }

    /**
     * Handles event change for the toggle slider component.
     */
    handleChangeEvent(): void {
        this.checked = !this.checked;

        this.propagateChange(this.checked);
    }

    /**
     * Run one change detection and mark the form field as touched,
     * so the error is shown if the form field is initialized with an invalid value.
     */
    ngAfterViewInit(): void {
        // If there are errors then trigger a new change detection cycle which marks the field as touched.
        if (this.errors && this.initValidation && this.value?.value) {
            setTimeout(() => {
                this.markAsTouched();
                this.value.updateValueAndValidity();
                this.cdr.detectChanges();
            });
        }
    }

    ngOnChanges(changes: SimpleChanges): void {
        // If the form is invalid then set the error message as a custom error.
        // Timeout required to show errors at init https://github.com/angular/angular/issues/19170#issuecomment-341555371
        if (changes.errors) {
            setTimeout(() => {
                this.value.setErrors(changes.errors.currentValue);
            });
        }

        if (this.submitted !== undefined) {
            // Check if we should trigger a detection cycle for the toggle slider.
            if (this.submitted && !this._submitted) {
                this._submitted = this.submitted;
                this.markAsTouched();
            }
        }
    }

    ngOnDestroy(): void {
        this.destroySubject.next();
        this.destroySubject.complete();
    }

    /**
     * Write value from form model into the view.
     */
    writeValue(value: any): void {
        if (value !== undefined) {
            this.checked = value;
        }
    }

    registerOnValidatorChange(fn: () => void): void {
        this._onChange = fn;
    }

    setDisabledState(disabled: boolean) {
        this.disabled = disabled;
    }

    /** Marks the form control as touched. */
    markAsTouched() {
        this.onTouched();
        this.value.markAsTouched();
        this.cdr.detectChanges();
    }

    /**
     * Returns form validation errors or null if form is valid.
     */
    validate(): ValidationErrors | null {
        if (!this.value.valid && this.errors) {
            return this.errors;
        }

        // tslint:disable-next-line: no-null-keyword
        return null;
    }

    _onChange: () => void = () => {};
    onTouched: () => void = () => {};
    propagateChange = (_: any) => {};

    /**
     * Register callback function as an internal method.
     */
    registerOnChange(fn: any): void {
        this.propagateChange = fn;
    }

    /**
     * Register callback function as an internal method.
     */
    registerOnTouched(fn: any): void {
        this.onTouched = fn;
    }
}

