    /**
     * Check if the button shows disabled styles.
     * Disabled button does not emit event.
     */
    @Input() disabled: boolean = false;

    /**
     * Label for the icon.
     */
    @Input() iconAriaLabel?: string;

    /**
     * Color support for the icon buttons.
     */
    @Input() color: IconButtonColor = 'primary-light';

    /**
     * Adds link support to button click.
     */
    @Input() url?: string | string[];

    /**
     * Allows url to be opened in new tab.
     */
    @Input() target?: '_blank';

    /**
     * Text for the button.
     */
    @Input() text?: string;

    /**
     * Emit the button click event.
     */
    @Output() buttonClick: EventEmitter<void> = new EventEmitter<void>();

    /**
     * Classes for the ngClass of the button.
     */
    get buttonClasses(): { [klass: string]: boolean } {
        return {
            'dd-white dd-primary-100-hover dd-primary-100-active': this.color === 'primary-light' && this.variant === 'basic', //
            'dd-accent dd-accent-darker-bg-hover dd-accent-darkest-bg-active': this.color === 'secondary' && this.variant === 'basic',
            'dd-primary-900 dd-primary-800-hover dd-primary-600-active': this.color === 'primary-dark' && this.variant === 'basic',
            'disabled ': this.disabled,
            'dd-primary-600-fg-active ': this.color === 'primary-dark' && this.variant === 'outline',
        };
    }

    /**
     * Classes for the text color of the button.
     */
    get textColorClasses(): { [klass: string]: boolean } {
        return {
            'dd-primary-300-fg': this.color === 'primary-light' && this.variant === 'basic', //
            'dd-primary-200-fg': this.color === 'primary-dark' && this.variant === 'basic',
            'dd-primary-100-fg': this.color === 'secondary' && this.variant === 'basic',
        };
    }

    /**
     * Classes for the ngClass of the button outline.
     */
    get buttonOutlineClasses(): { [klass: string]: boolean } {
        return {
            'dd-primary-900-fg': this.color === 'primary-light' && this.variant === 'outline', //
            'dd-accent-fg': this.color === 'secondary' && this.variant === 'outline',
            'dd-primary-50-fg ': this.color === 'primary-dark' && this.variant === 'outline',
            'disabled ': this.disabled,
        };
    }

    /**
     * Classes for the icon outline.
     */
    get iconOutlineClasses(): { [klass: string]: boolean } {
        return {
            'dd-primary-900-border': this.color === 'primary-light' && this.variant === 'outline', //
            'dd-accent-border': this.color === 'secondary' && this.variant === 'outline',
            'dd-primary-50-border': this.color === 'primary-dark' && this.variant === 'outline',
            'p-spacer-solo': this.size === 'normal' || this.size === 'mini',
            'p-spacer-xmini': this.size === 'large',
        };
    }
}

