@use '../../../../styles/helpers/hover';
@use '../../../../styles/variables/opacity';
@use '../../../../styles/themes/themify';

a,
button {
    text-align: center;

    cursor: pointer;
    text-decoration: none !important;
    border: none !important;
    border-radius: 6px;

    @include themify.dd-themify() {
        $iconButtonHeight: themify.dd-configuration('inputs', default-size);

        height: $iconButtonHeight;
        max-height: $iconButtonHeight;
        min-height: $iconButtonHeight;

        width: $iconButtonHeight;
        max-width: $iconButtonHeight;
        min-width: $iconButtonHeight;
    }

    &:active {
        box-shadow: none !important;
    }
    &:disabled,
    &.disabled {
        cursor: default;
        box-shadow: unset !important;
        opacity: opacity.$opacity-button-disabled !important;
        pointer-events: none !important;
    }

    @include hover.hover-transition();

    .icon-button-text {
        font-size: 8px;
        margin-top: -4px;
    }
}

.icon-button-outline {
    background-color: transparent !important;
    height: unset;
    max-height: unset;
    min-height: unset;

    width: unset;
    max-width: unset;
    min-width: unset;
    max-height: unset;

    &:hover {
        * {
            filter: brightness(80%);
        }
        &.dd-primary-900-fg * {
            @include themify.dd-themify() {
                color: themify.dd-color('primary-300') !important;
            }
        }
        .border.dd-primary-900-border {
            @include themify.dd-themify() {
                border-color: themify.dd-color('primary-300') !important;
                color: themify.dd-color('primary-300') !important;
            }
        }
    }
    &:active {
        * {
            filter: brightness(70%);
        }
        &.dd-primary-900-fg * {
            @include themify.dd-themify() {
                color: themify.dd-color('primary-200') !important;
            }
        }
        .border.dd-primary-900-border {
            @include themify.dd-themify() {
                border-color: themify.dd-color('primary-200') !important;
                color: themify.dd-color('primary-200') !important;
            }
        }
    }

    @include hover.hover-transition();
}

