import { ChangeDetectionStrategy, Component, EventEmitter, Input, Output } from '@angular/core';

/** Type for the color of the icon button. */
export type IconClickableColor = 'primary-light' | 'primary-dark' | 'secondary-light' | 'secondary-dark' | 'white';

type StyleDict = {
    [key in IconClickableColor]: string;
};

const styleClasses: StyleDict = {
    'primary-light': 'dd-primary-500-fg dd-accent-darker-fg-hover dd-accent-darkest-fg-active',
    'primary-dark': 'dd-primary-400-fg dd-primary-200-fg-hover dd-primary-100-fg-active',
    'secondary-light': 'dd-accent-fg dd-accent-darker-fg-hover dd-accent-darkest-fg-active',
    'secondary-dark': 'dd-accent-fg dd-primary-200-fg-hover dd-primary-100-fg-active',
    white: 'dd-white-fg dd-accent-darker-fg-hover dd-accent-darkest-fg-active',
};

/**
 * Component for an icon that triggers a click event.
 */
@Component({
    selector: 'dd-icon-clickable',
    templateUrl: './icon-clickable.component.html',
    styleUrls: ['./icon-clickable.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class IconClickableComponent {
    /**
     * Icon for the button.
     * Supports Design library icons.
     */
    @Input() icon?: string;

    /**
     * Check if the button shows disabled styles.
     * Disabled button does not emit event.
     */
    @Input() disabled: boolean = false;

    /**
     * Label for the icon.
     */
    @Input() iconAriaLabel?: string;

    /**
     * Color support for the icon buttons.
     */
    @Input() color: IconClickableColor = 'primary-light';

    /**
     * Color support for the icon buttons.
     */
    @Input() backgroundColor: undefined | 'light' = undefined;

    /**
     * Emit the button click event.
     */
    @Output() buttonClick: EventEmitter<void> = new EventEmitter<void>();

    /**
     * Picks style class based on color.
     */
    get buttonClasses(): string {
        const colorStyles = styleClasses[this.color];
        return `${colorStyles} ${this.backgroundColor}`;
    }
}

