import { ChangeDetectionStrategy, Component, Input } from '@angular/core';

/**
 * Component for displaying an icon.
 */
@Component({
    selector: 'dd-icon',
    templateUrl: './icon.component.html',
    styleUrls: ['./icon.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class IconComponent {
    /**
     * Icon class for the i tag.
     */
    @Input() icon?: string;

    /**
     * Size supports icon size helper class "s-X" where X supports values from 4 to 256 with increments of 2.
     * Defaults to configured default icon size.
     */
    @Input() size?: string;

    /** Label for the icon  */
    @Input() iconAriaLabel?: string;

    /**
     * The default size variant for the icon.
     */
    @Input() variant: 'default' | 'mini' = 'default';

    /**
     * Generates the class name for the icon based on the provided icon.
     */
    get iconClassName(): string {
        if (!this.icon) {
            return '';
        }

        if (this.icon.startsWith('dd-icon-')) {
            return this.icon;
        }

        if (this.icon.startsWith('fa') || this.icon.includes('fa-')) {
            return this.icon;
        }

        return '';
    }

    /**
     * Determines whether the icon should be hidden from accessibility based on the presence of an aria label.
     */
    get iconAriaHidden(): boolean | null {
        return !!this.iconAriaLabel ? false : true;
    }
}

