import { ChangeDetectionStrategy, Component, EventEmitter, Input, Output } from '@angular/core';

/** The variant for the component. Default 'basic' is 24px and 'mini' is 16px. */
export type InputComponentType = 'checkbox' | 'radio';

/** The variant for the component. Default 'basic' is 24px and 'mini' is 16px. */
export type InputComponentVariant = 'basic' | 'mini';

/**
 * Component for handling input elements such as checkboxes and radio buttons.
 */
@Component({
    selector: 'dd-input',
    templateUrl: './input.component.html',
    styleUrls: ['./input.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class InputComponent {
    /**
     * The type of input component.
     * Supports 'checkbox' and 'radio'.
     */
    @Input() type: InputComponentType = 'checkbox';

    /** Label for the input. */
    @Input() inputLabel?: string;

    /** Name can be used to define radio button group. */
    @Input() inputName?: string;

    /** If the input is checked. */
    @Input() checked: boolean = false;

    /** If the input is disabled. */
    @Input() disabled: boolean = false;

    /**
     * Add support for 24px and 16px checkboxes.
     * The height of the elements are 32px and 24px.
     * Defaults to 'basic'.
     */
    @Input() variant: InputComponentVariant = 'basic';

    /**
     * Emits an click event for the input.
     * Parent should always handle the event and switch the inputs for the component.
     */
    @Output() private inputClick: EventEmitter<void> = new EventEmitter();

    readonly uniqueId: string = Math.random().toString(36).substring(2);

    /**
     * Handles event change for the component.
     * First makes sure that the input's target.checked matches to the input's one so there is no chance that parent and this component are unsynced.
     * Then outpus the input click event.
     */
    handleChangeEvent(event: any): void {
        // Cancel the assingment of the checked variable in the scope of this component.
        // eslint-disable-next-line eqeqeq
        if (event?.target?.checked != undefined) {
            event.target.checked = this.checked;
        }

        // Emit the input click event to the parent.
        this.inputClick.emit();
    }
}

