@use '../../../../styles/themes/themify';
@use '../../../../styles/helpers/hover';

.b-contain *,
.b-contain *::before,
.b-contain *::after {
    box-sizing: content-box !important;
}

.b-contain input {
    position: absolute;
    z-index: -1;
    opacity: 0;
}

.b-contain span {
    font-size: 1rem;
    font-family: inherit;
}

.b-contain {
    display: flex;
    position: relative;
    cursor: pointer;
    width: 100%;
    padding-left: 36px;
    height: 100%;
    min-height: 32px;
}

.disabled {
    opacity: 0.6 !important;
    label {
        cursor: default !important;
    }
}

.b-contain input[type='checkbox'] ~ .b-input {
    position: absolute;
    top: 4px;
    left: 0;
    height: 24px;
    width: 24px;
    transition: background 250ms;
    border: 1px solid;
    border-radius: 6px;

    @include themify.dd-themify() {
        background-color: themify.dd-color('white') !important;
        border-color: themify.dd-color('primary-200') !important;
    }
}

.b-contain input[type='radio'] ~ .b-input {
    position: absolute;
    top: 4px;
    left: 0;
    height: 24px;
    width: 24px;
    transition: background 250ms;
    border: 1px solid;
    border-radius: 100%;

    @include themify.dd-themify() {
        background-color: themify.dd-color('white') !important;
        border-color: themify.dd-color('accent') !important;
    }
}

.b-contain input[type='checkbox'] ~ .b-input::after {
    content: '';
    position: absolute;
    display: none;
    top: 5px;
    left: 9px;
    height: 9px;
    width: 5px;
    border: 1px solid;
    border-width: 0 2px 2px 0;
    transition: background 250ms;
    transform: rotate(45deg);

    @include themify.dd-themify() {
        border-color: themify.dd-color-contrast('accent') !important;
    }
}

.b-contain input[type='checkbox']:checked ~ .b-input {
    @include themify.dd-themify() {
        border-color: themify.dd-color('accent') !important;
    }
}

.b-contain input[type='checkbox']:checked:disabled ~ .b-input {
    @include themify.dd-themify() {
        border-color: themify.dd-color('primary-200') !important;
    }
}

.b-contain input[type='radio'] ~ .b-input::after {
    content: '';
    position: absolute;
    display: none;
    left: 4px;
    top: 4px;
    width: 16px;
    height: 16px;
    border-radius: 100%;
    transition: background 250ms;

    @include themify.dd-themify() {
        background-color: themify.dd-color('accent') !important;
    }
}

.b-contain input[type='checkbox']:disabled ~ .b-input::after {
    @include themify.dd-themify() {
        border-color: themify.dd-color-contrast('accent') !important;
    }
}

.b-contain input[type='checkbox']:disabled:checked ~ .b-input {
    @include themify.dd-themify() {
        background-color: themify.dd-color('primary-200') !important;
    }
}

.b-contain input:checked ~ .b-input::after {
    display: block;
}

.b-contain input:not([disabled]):hover ~ .b-input {
    @include hover.hover-transition();

    @include themify.dd-themify() {
        box-shadow: 0 0 0 4px themify.dd-color('primary-100') !important;
    }
}

.b-contain input:not([disabled]):focus ~ .b-input {
    @include themify.dd-themify() {
        box-shadow: 0 0 0 4px themify.dd-color('primary-100') !important;
    }
}

.b-contain input[type='checkbox']:checked ~ .b-input {
    @include themify.dd-themify() {
        background-color: themify.dd-color('accent') !important;
    }
}

.b-contain input[type='radio']:checked ~ .b-input {
    @include themify.dd-themify() {
        background-color: themify.dd-color('white') !important;
    }
}

.b-contain input[type='radio']:disabled ~ .b-input {
    @include themify.dd-themify() {
        border-color: themify.dd-color('primary-200') !important;
    }
}

.b-contain input[type='radio']:disabled ~ .b-input::after {
    @include themify.dd-themify() {
        background-color: themify.dd-color('primary-200') !important;
    }
}

.b-contain .b-input::before {
    content: '';
    display: block;
    position: absolute;
    left: 0;
    top: 0;
    width: 3rem;
    height: 3rem;
    margin-left: -0.85rem;
    margin-top: -0.85rem;
    border-radius: 2rem;
    opacity: 0.6;
    transform: scale(0);
}

@keyframes b-ripple {
    0% {
        transform: scale(0);
    }

    20% {
        transform: scale(1);
    }

    100% {
        opacity: 0;
        transform: scale(1);
    }
}

@keyframes b-ripple-duplicate {
    0% {
        transform: scale(0);
    }

    30% {
        transform: scale(1);
    }

    60% {
        transform: scale(1);
    }

    100% {
        opacity: 0;
        transform: scale(1);
    }
}

.b-contain input + .b-input::before {
    animation: b-ripple 250ms ease-out;
}

.b-contain input:checked + .b-input::before {
    animation-name: b-ripple-duplicate;
}

.b-contain .b-input::before {
    visibility: hidden;
}

.b-contain input:focus + .b-input::before {
    visibility: visible;
}

.b-contain:first-child .b-input::before {
    visibility: hidden;
}

.dd-checkbox-basic {
    min-height: 32px !important;
}
// Add support for mini inputs.
.dd-checkbox-mini {
    min-height: 24px !important;

    .b-contain {
        padding-left: 26px;
        min-height: 24px;
    }

    .b-contain span {
        // This should match input height.
        line-height: 16px;
    }

    .b-contain {
        input[type='checkbox'] ~ .b-input {
            top: 3px;
            height: 16px;
            width: 16px;

            &::after {
                top: 3px;
                left: 6px;
                height: 6px;
                width: 3px;
            }
        }

        input[type='radio'] ~ .b-input {
            top: 3px;
            height: 16px;
            width: 16px;

            &::after {
                left: 3px;
                top: 3px;
                width: 10px;
                height: 10px;
            }
        }
    }
}

