    /** If the input is checked. */
    @Input() checked: boolean = false;

    /** If the input is disabled. */
    @Input() disabled: boolean = false;

    /** If the content uses cursor: pointer. Reasons for having this as default would be
     *  e.g content including a link that needs to be differentiated from rest of the text. Cursor: pointer indicates more clearly that whole
     * content is clickable.  */
    @Input() cursor?: 'pointer' | 'default' = 'default';

    /**
     * Add support for 24px and 16px checkboxes.
     * The height of the elements are 32px and 24px.
     * Defaults to 'basic'.
