        background-color: themify.dd-color('white') !important;
    }
}

.b-contain input[type='radio']:disabled ~ .b-input {
    @include themify.dd-themify() {
        border-color: themify.dd-color('primary-200') !important;
    }
}

.b-contain input[type='radio']:disabled ~ .b-input::after {
    @include themify.dd-themify() {
        background-color: themify.dd-color('primary-200') !important;
    }
}

.b-contain .b-input::before {
    content: '';
    display: block;
    position: absolute;
    left: 0;
    top: 0;
    width: 3rem;
    height: 3rem;
    margin-left: -0.85rem;
    margin-top: -0.85rem;
    border-radius: 2rem;
    opacity: 0.6;
    transform: scale(0);
}

@keyframes b-ripple {
    0% {
        transform: scale(0);
    }

    20% {
        transform: scale(1);
    }

    100% {
        opacity: 0;
        transform: scale(1);
    }
}

@keyframes b-ripple-duplicate {
    0% {
        transform: scale(0);
    }

    30% {
        transform: scale(1);
    }

    60% {
        transform: scale(1);
    }

    100% {
        opacity: 0;
        transform: scale(1);
    }
}

.b-contain input + .b-input::before {
    animation: b-ripple 250ms ease-out;
}

.b-contain input:checked + .b-input::before {
    animation-name: b-ripple-duplicate;
}

.b-contain .b-input::before {
    visibility: hidden;
}

.b-contain input:focus + .b-input::before {
    visibility: visible;
}

.b-contain:first-child .b-input::before {
    visibility: hidden;
}

.dd-checkbox-basic {
    min-height: 32px !important;
}
// Add support for mini inputs.
.dd-checkbox-mini {
    min-height: 24px !important;

    .b-contain {
        padding-left: 26px;
        min-height: 24px;
    }

    .b-contain span {
        // This should match input height.
        line-height: 16px;
    }

    .b-contain {
        input[type='checkbox'] ~ .b-input {
            top: 3px;
            height: 16px;
            width: 16px;

            &::after {
                top: 3px;
                left: 6px;
                height: 6px;
                width: 3px;
            }
        }

        input[type='radio'] ~ .b-input {
            top: 3px;
            height: 16px;
            width: 16px;

            &::after {
                left: 3px;
                top: 3px;
                width: 10px;
                height: 10px;
            }
        }
    }
}

:host {
    width: 100%;
}

// Span for radio inputs should be centered also when there are multiple lines
.b-contain input[type='radio'] ~ .b-input {
    top: auto;
}

// Align input element with b-input
.b-contain input[type='checkbox'] {
    top: 4px;
}

// Overriding some properties from input.component.scss to keep styling while making only input clickable.
.b-contain input {
    z-index: 1;
    left: 0;
    height: 25px;
    width: 25px;
    cursor: pointer;
}

.b-contain {
    &:not(.pointer) {
        cursor: default;
    }
}

