import { ChangeDetectionStrategy, Component, Input } from '@angular/core';
import { LoaderSpinnerSizes } from '../loader-spinner/loader-spinner.component';

/**
 * Component for displaying a loading overlay with a spinner and optional text.
 */
@Component({
    selector: 'dd-loader-overlay',
    templateUrl: './loader-overlay.component.html',
    styleUrls: ['./loader-overlay.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class LoaderOverlayComponent {
    /**
     * Determines if the spinner is shown.
     * If false then renders the content with ng-content.
     */
    @Input() loading: boolean = false;

    /**
     * Adds a text under the loader spinner.
     * Used mainly for giving user info what is going on.
     */
    @Input() text?: string;

    /**
     * Defines the diameter for the loader element.
     * Supports the spacer element sizes and 128px for extra big loaders.
     */
    @Input() diameter?: LoaderSpinnerSizes;

    /**
     * Allows user to define a custom class for the spinner container.
     */
    @Input() spinnerContainerClass?: string;

    /**
     * Defines the background color for the overlay.
     */
    @Input() backgroundColor: string = 'dd-white-bg';

    /**
     * Calculate the border size, the original spinner had 120px height and width and 16px borders.
     */
    get loaderBorderSizes(): { [klass: string]: any } {
        if (!this.diameter) {
            return {};
        }

        const borderSize: number = this.diameter / 8;

        return {
            'border-width': `${borderSize}px`,
            width: `${this.diameter}px`,
            height: `${this.diameter}px`,
        };
    }
}

