import { Component, EventEmitter, Input, OnDestroy, Output } from '@angular/core';
import { OverlayOpenModel, OverlayService } from '@digitaldelightsoy/design-library/src/overlay';
import { Subject } from 'rxjs';
import { delay, take, takeUntil } from 'rxjs/operators';
import { SelectItem, SelectListModel } from '../../select-models';
import { SelectMenuComponent, SelectMenuOutput } from '../select-menu/select-menu.component';

@Component({
    selector: 'dd-multi-select-list',
    templateUrl: './multi-select-list.component.html',
    styleUrls: ['./multi-select-list.component.scss'],
})
export class MultiSelectListComponent implements OnDestroy {
    constructor(private overlayService: OverlayService) {}
    /**
     * Label to be shown if used in a form.
     */
    @Input() label?: string;
    /**
     * List of items that are selectable.
     */
    @Input() items: SelectItem<any>[] = [];

    /**
     * List of selected items.
     */
    @Input() selected?: (string | number)[];

    /**
     * Text to be shown when there is no selected item.
     */
    @Input() selectText: string = 'Select';

    /**
     * Text that is shown when there are multiple selected.
     * Appends the count before this text item.
     */
    @Input() multipleSelectionText: string = 'selected';

    /**
     * If the search is enabled.
     */
    @Input() searchEnabled: boolean = false;

    /**
     * Placeholder for the search.
     */
    @Input() searchPlaceHolder: string = '';

    /**
     * Shows text if search does not have any results.
     */
    @Input() searchNoResultsMessage: string = 'No results';

    /**
     * Allows for use of custom icons.
     */
    @Input() icon?: string = 'dd-icon-arrow-down';

    /**
     * If you are using the element on different background color than white, you can change the background color of the input.
     * Use color classes that define both background and font color from the library.
     * Default is white background, with white some fonts are using lighter gray color than the contrast is.
     */
    @Input() backgroundColor: string = 'dd-white';

    /**
     * Outputs which list item was clicked and the whole list.
     */
    @Output() itemClick: EventEmitter<[SelectItem<any>, (string | number)[]]> = new EventEmitter();

    /**
     * Reference to the opened modal.
     */
    overlayModel?: OverlayOpenModel<SelectMenuComponent>;

    private readonly _unsubscribeAllSubject: Subject<undefined> = new Subject();

    get anchorText(): string | number {
        if (this.selected === null || this.selected === undefined) {
            return this.selectText;
        }

        // If there are multiple selections made then we need to show it.
        if (this.selected && this.selected.length > 1) {
            return `${this.selected.length} ${this.multipleSelectionText}`;
        }

        return this.items.find((x) => this.selected?.some((y) => x.id === y))?.text ?? this.selectText;
    }

    /** Completes the exposed subject. */
    ngOnDestroy(): void {
        this._unsubscribeAllSubject.next(undefined);
        this._unsubscribeAllSubject.complete();
    }

    onAnchorClick(element: HTMLDivElement): void {
        // Close existing overlay
        if (!!this.overlayModel) {
            this.closeMenu();
            return;
        }

        const widthOfMenuelement: number = element.offsetWidth;

        const data: SelectListModel = {
            items: this.items,
            selected: this.selected,
            searchEnabled: this.searchEnabled,
            searchNoResultsMessage: this.searchNoResultsMessage,
            searchPlaceholder: this.searchPlaceHolder,
            variant: 'basic',
        };

        const config = this.overlayService.getConnectedToConfig<SelectListModel>(element, '.dd-select-menu', data);

        if (config.overlayConfig) {
            config.overlayConfig.minWidth = widthOfMenuelement;
            config.closeOnOutsidePointerEvents = true;
        }

        this.overlayModel = this.overlayService.open(SelectMenuComponent, config);

        this.overlayModel.component.itemClick
            .pipe(
                takeUntil(this._unsubscribeAllSubject) //
            )
            .subscribe((selection: SelectMenuOutput) => {
                this.itemClick.emit(selection);
            });

        this.overlayModel.overlayRef
            .detachments()
            .pipe(
                // Add delay so when clicking anchor (that is outside event) the anchor click happens first and doesnt open a new overlay.
                delay(1),
                take(1),
                takeUntil(this._unsubscribeAllSubject)
            )
            .subscribe(() => {
                this.closeMenu();
            });
    }

    /** Helper function to get rid of things. */
    private closeMenu(): void {
        this.overlayModel?.close();
        delete this.overlayModel;
    }
}

