import { BreakpointObserver, BreakpointState } from '@angular/cdk/layout';
import { Component, ElementRef, EventEmitter, Input, OnDestroy, Output, Renderer2 } from '@angular/core';
import { NavigationEnd, Router } from '@angular/router';
import { Subject, debounceTime, filter, takeUntil } from 'rxjs';
import { DdBreakPointsEnum, NavigationItem } from '../../models/navigation-models';
import { TopbarConfiguration } from '../toolbar-top/toolbar-top.component';

@Component({
    selector: 'dd-navigation',
    templateUrl: './navigation.component.html',
    styleUrls: ['./navigation.component.scss'],
})
export class NavigationComponent implements OnDestroy {
    constructor(breakpointObserver: BreakpointObserver, private renderer: Renderer2, private elementRef: ElementRef, router: Router) {
        breakpointObserver
            .observe([this.lockedOpenBreakpoint, this.fullWidthBreakpoint])
            .pipe(debounceTime(50), takeUntil(this._unsubscribeAllSubject))
            .subscribe((state: BreakpointState) => {
                this.sidebarLockedOpen = !!state.breakpoints[this.lockedOpenBreakpoint];
                this.sidebarFullWidth = !!state.breakpoints[this.fullWidthBreakpoint];

                if (this.sidebarLockedOpen) {
                    this.openSidebar();
                }
            });

        // Close the sidebar on navigation if it's open and is not locked open.
        router.events
            .pipe(
                filter((x) => x instanceof NavigationEnd),
                takeUntil(this._unsubscribeAllSubject)
            )
            .subscribe(() => {
                if (!this.sidebarLockedOpen && this.sidebarOpen) {
                    this.closeSidebar();
                }
            });
    }

    /**
     * Navigational items for sidebar.
     */
    @Input() navigation?: NavigationItem[];

    /**
     * Items marked as favorites from the sidebar.
     */
    @Input() favoriteNavigation?: NavigationItem[];

    /**
     * The full configuration for rendering different parts of the top bar.
     */
    @Input() topbarConfiguration?: TopbarConfiguration;

    /**
     * If the side bar should be hidden.
     * For instance, some projects might want to hide the whole sidebar if user is not logged in.
     */
    @Input() hideSidebar: boolean = false;

    /**
     * This switch indicates if the solution should use Design library's side navigation or a custom one.
     * The menu button is shown always when using custom navigation.
     *
     * NOTE: This should be used only during migration for applications that have custom navigation.
     */
    @Input() useCustomNavigation: boolean = false;

    /**
     * Output event when menu button is clicked.
     */
    @Output() menuClick: EventEmitter<void> = new EventEmitter();

    /**
     * Click event for the navigation items. For instance, this is useful for analytics.
     */
    @Output() navigationClick: EventEmitter<NavigationItem> = new EventEmitter();

    /**
     * Output event that item is marked as favorite.
     */
    @Output() markAsFavorite: EventEmitter<NavigationItem> = new EventEmitter();

    /**
     * Breakpoint where the navigation should be locked open.
     */

    private readonly lockedOpenBreakpoint: string = DdBreakPointsEnum['gt-lg'];

    private readonly fullWidthBreakpoint: string = DdBreakPointsEnum['lt-sm'];

    sidebarOpen?: boolean;
    sidebarLockedOpen?: boolean;

    private sidebarFullWidth: boolean = false;
    private sidebarBackDrop?: HTMLElement;

    /**
     * Unsubscribe subject for handling the subscriptions.
     */
    // eslint-disable-next-line rxjs/no-exposed-subjects
    private readonly _unsubscribeAllSubject: Subject<undefined> = new Subject();

    /** Completes the exposed subject. */
    ngOnDestroy(): void {
        this._unsubscribeAllSubject.next(undefined);
        this._unsubscribeAllSubject.complete();
    }

    toggleSidebarOpen(): void {
        if (this.useCustomNavigation) {
            this.menuClick.next();
        }

        if (this.sidebarOpen) {
            this.closeSidebar();
        } else {
            this.openSidebar();
        }
    }

    /**
     * Close the sidebar
     */
    private closeSidebar(): void {
        if (!this.sidebarOpen || this.sidebarLockedOpen) {
            return;
        }

        // Hide the backdrop
        this.hideBackdrop();

        // Set the opened status
        this.sidebarOpen = false;
    }

    /**
     * Open the sidebar
     */
    private openSidebar(): void {
        if (this.sidebarOpen || this.sidebarLockedOpen) {
            if (this.sidebarLockedOpen) {
                this.hideBackdrop();
            }

            return;
        }

        // Show the backdrop
        this.showBackdrop();

        // Set the opened status
        this.sidebarOpen = true;
    }

    /**
     * Show the backdrop
     */
    private showBackdrop(): void {
        if (this.sidebarFullWidth) {
            this.hideBackdrop();
            return;
        }

        // Create the backdrop element
        this.sidebarBackDrop = this.renderer.createElement('div');

        // Add a class to the backdrop element
        this.sidebarBackDrop?.classList.add('sidebar-backdrop-overlay');

        if (this.sidebarBackDrop?.style) {
            this.sidebarBackDrop.style.position = 'fixed';
            this.sidebarBackDrop.style.top = '0';
            this.sidebarBackDrop.style.bottom = '0';
            this.sidebarBackDrop.style.left = '0';
            this.sidebarBackDrop.style.right = '0';
            this.sidebarBackDrop.style.zIndex = '999';
            this.sidebarBackDrop.style.backgroundColor = 'rgba(0, 0, 0, 0.6)';
        }

        // Append the backdrop to the parent of the sidebar
        this.renderer.appendChild(this.elementRef.nativeElement, this.sidebarBackDrop);

        // Add an event listener to the overlay
        this.sidebarBackDrop?.addEventListener('click', () => {
            this.closeSidebar();
        });
    }

    /**
     * Hide the backdrop
     */
    private hideBackdrop(): void {
        if (!this.sidebarBackDrop) {
            return;
        }

        // Remove the backdrop
        this.renderer?.removeChild(this.elementRef.nativeElement, this.sidebarBackDrop);
        delete this.sidebarBackDrop;
    }
}

