import { Component, EventEmitter, HostBinding, Input, OnDestroy, OnInit, Output, ViewEncapsulation } from '@angular/core';
import { Event, NavigationEnd, Router } from '@angular/router';
import { Subject } from 'rxjs';
import { filter, takeUntil } from 'rxjs/operators';
import { NavigationItem } from '../../models/navigation-models';

@Component({
    selector: 'dd-navigation-item-collapsable',
    templateUrl: './navigation-item-collapsable.component.html',
    encapsulation: ViewEncapsulation.None,
})
export class NavigationItemCollapsableComponent implements OnInit, OnDestroy {
    constructor(private _router: Router) {}

    /**
     * Contains all the necessary data for rendering a navigation item.
     */
    @Input() item?: NavigationItem;

    /**
     * Click event for the navigation items. For instance, this is useful for analytics.
     */
    @Output() navigationClick: EventEmitter<NavigationItem> = new EventEmitter();

    /**
     * Output event that item is marked as favorite.
     */
    @Output() markAsFavorite: EventEmitter<NavigationItem> = new EventEmitter();

    @HostBinding('class') classes: string = 'nav-collapsable nav-item';
    @HostBinding('class.open') isOpen: boolean = false;

    private readonly _unsubscribeAllSubject: Subject<undefined> = new Subject();

    childrenIsActive: boolean = false;

    ngOnInit(): void {
        if (!this.item) {
            throw new Error('No @Input item is defined.');
        }

        // Listen for router events
        this._router.events
            .pipe(
                filter((event) => event instanceof NavigationEnd),
                takeUntil(this._unsubscribeAllSubject)
            )
            .subscribe((event: Event) => {
                if (!this.item) {
                    console.warn('No @Input item is defined.');
                    return;
                }

                // Check if the url can be found in one of the children of this item
                if (this.isUrlInChildren(this.item, (event as NavigationEnd).urlAfterRedirects)) {
                    this.childrenIsActive = true;
                    this.expand();
                } else {
                    this.childrenIsActive = false;
                    this.collapse();
                }
            });

        // Check if the url can be found in one of the children of this item
        if (this.isUrlInChildren(this.item, this._router.url)) {
            this.childrenIsActive = true;
            this.expand();
        } else {
            this.childrenIsActive = false;
            this.collapse();
        }
    }

    ngOnDestroy(): void {
        this._unsubscribeAllSubject.next(undefined);
        this._unsubscribeAllSubject.complete();
    }

    /**
     * Toggle collapse
     */
    toggleOpen(ev: MouseEvent): void {
        ev.preventDefault();

        this.isOpen = !this.isOpen;
    }

    /**
     * Expand the collapsable navigation
     */
    private expand(): void {
        if (this.isOpen) {
            return;
        }

        this.isOpen = true;
    }

    /**
     * Collapse the collapsable navigation
     */
    private collapse(): void {
        if (!this.isOpen) {
            return;
        }

        this.isOpen = false;
    }

    /**
     * Check if the given url can be found
     * in one of the given parent's children
     */
    private isUrlInChildren(parent: NavigationItem, url: string): boolean {
        const children: NavigationItem[] | undefined = parent.children;

        if (!children) {
            return false;
        }

        for (const child of children) {
            if (child.children) {
                if (this.isUrlInChildren(child, url)) {
                    return true;
                }
            }

            if (child.url && (child.url === url || url.includes(child.url))) {
                return true;
            }
        }

        return false;
    }
}

import { ChangeDetectionStrategy, Component, EventEmitter, HostBinding, Input, Output, ViewEncapsulation } from '@angular/core';
import { NavigationItem } from '../../models/navigation-models';

@Component({
    selector: 'dd-navigation-item',
    templateUrl: './navigation-item.component.html',
    styleUrls: ['./navigation-item.component.scss'],
    encapsulation: ViewEncapsulation.None,
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class NavigationItemComponent {
    /**
     * Contains all the necessary data for rendering a navigation item.
     */
    @Input() item?: NavigationItem;

    @Input() isFavoriteList: boolean = false;

    /**
     * Click event for the navigation items. For instance, this is useful for analytics.
     */
    @Output() navigationClick: EventEmitter<NavigationItem> = new EventEmitter();

    /**
     * Output event that item is marked as favorite.
     */
    @Output() markAsFavorite: EventEmitter<NavigationItem> = new EventEmitter();

    /**
     * Binds the class of the host element to the component.
     */
    @HostBinding('class') classes: string = 'nav-item';

    /**
     * Stops event propagation and emits the navigationClick event.
     * Emits the markAsFavorite event if the item is marked as favorite.
     */
    markAsFavoriteClick(event: MouseEvent): void {
        if (!this.item) {
            return;
        }

        event.preventDefault();
        event.stopPropagation();
        this.markAsFavorite.emit(this.item);
    }
}

