import { Component, ElementRef, HostBinding, Input } from '@angular/core';
import { OverlayAnimationState, overlayAnimations } from '../../models/animations';

/**
 * Wrapper component for overlay. This component provides the overlay with a slideFromTop
 * animation and the classes used for the overlay panel. Component also sets flex direction for parent component to keep a flex-column layout.
 * For a dialog wrapper with a slideFromRight animation, use DialogWrapperComponent.
 */
@Component({
    selector: 'dd-overlay-wrapper',
    template: '<ng-content></ng-content>',
    // To work with scrollable overlay, "scrollable" class can to be added only when
    // animation is finished, or otherwise the animation wont work due to overflow being hidden.
    // eslint-disable-next-line @angular-eslint/no-host-metadata-property
    host: {
        '(@slideFromTop.start)': 'captureStartEvent($event)',
        '(@slideFromTop.done)': 'captureDoneEvent($event)',
    },
    animations: [overlayAnimations.slideFromTop],
})
export class OverlayWrapperComponent {
    constructor(private elRef: ElementRef) {}
    @Input() viewState?: OverlayAnimationState;

    @HostBinding('class') klass = 'dd-white-bg border-radius-6 elevation-05 h-100-p';

    // Keeping correct layout for the overlay.
    @HostBinding('style.display') style_display = 'flex';
    @HostBinding('style.flexDirection') get style_flexDirection() {
        return 'column';
    }

    // If no viewstate is provided, give a default state to keep position.
    @HostBinding('@slideFromTop') get slideFromTopState(): OverlayAnimationState | false {
        if (this.viewState) {
            return this.viewState;
        } else {
            return 'default';
        }
    }

    // When no animation is used, disable trigger.
    @HostBinding('@.disabled') get hasAnimation(): boolean {
        if (!this.viewState) {
            return true;
        } else {
            return false;
        }
    }

    isScrollableOverlay: boolean = false;

    captureStartEvent(event: AnimationEvent) {
        const el = this.elRef.nativeElement.parentElement.parentElement;
        // Check if overlay should be scrollable, and mark it as such.
        if (this.viewState && el.classList.contains('scrollable')) {
            this.isScrollableOverlay = true;
            // Disable the overflow: overlay style for now.
            el.classList.remove('scrollable');
        }
    }

    captureDoneEvent(event: AnimationEvent) {
        const el = this.elRef.nativeElement.parentElement.parentElement;

        // When scrollable overlay is in view, add class again.
        if (this.viewState && this.viewState !== 'hide' && this.isScrollableOverlay) {
            el.classList.add('scrollable');
        }
    }
}

import { ChangeDetectionStrategy, Component, EventEmitter, Input, OnChanges, Output } from '@angular/core';

@Component({
    selector: 'dd-pagination',
    templateUrl: './pagination.component.html',
    styleUrls: ['./pagination.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class PaginationComponent implements OnChanges {
    /** The number of items displayed on each page. */
    @Input() pageSize: number = 0;
    /** The start index of pagination.*/
    @Input() pageIndex: number = 0;
    /** Total number of items that determine the pagecount when divided by pageSize. */
    @Input() items: number = 0;

    @Input() isMobile: boolean = false;

    @Output() pageChange: EventEmitter<number> = new EventEmitter();

    /* The array of pages currently shown in the paginator. */
    displayedPages: (number | undefined)[] = [];

    activePage: number = 0;
    pageCount: number = 0;

    private readonly maxVisiblePages: number = 7;
    private readonly startIndexat0: number = 3;
    private readonly pagesBeforeAndAfterEllipses: number = 5;
    private readonly arrayLengthBeforeAndAfterEllipses: number = 4;

    ngOnChanges(): void {
        this.activePage = this.pageIndex;
        this.pageCount = this.getPageCount();
        this.displayedPages = this.getDisplayedPages();
    }

    private getPageCount(): number {
        let countedPages = 0;

        if (this.items > 0 && this.pageSize > 0) {
            const pageCount = this.items / this.pageSize;
            const roundedPageCount = Math.floor(pageCount);

            // If rounded pageCount is smaller than actual pageCount, add 1 so that all items will be shown.
            countedPages = roundedPageCount < pageCount ? roundedPageCount + 1 : roundedPageCount;
        }
        return countedPages;
    }

    private getIndex(): number {
        // Indexing should start at 0 when active page is 4 or less than, to always show pages 1-5. Same thing if there are seven or less pages.
        if (this.activePage <= this.startIndexat0 || this.pageCount <= this.maxVisiblePages) {
            return 0;
        }

        // If the last set of pages is 5 or smaller, or last page is clicked, move index backwards to always display the last 5 pages.
        if ((this.pageCount - this.activePage < this.pagesBeforeAndAfterEllipses && this.activePage !== this.pageCount) || this.activePage === this.pageCount) {
            // When navigating to first number of last set, show previous number.
            if (this.pageCount - this.activePage === this.pagesBeforeAndAfterEllipses) {
                return this.pageCount - (this.pagesBeforeAndAfterEllipses - 1);
            }
            return this.pageCount - this.pagesBeforeAndAfterEllipses;
        }

        return this.activePage - 1;
    }

    private getDisplayedPages(): (number | undefined)[] {
        const pageArray: (number | undefined)[] = [];

        if (this.pageCount >= 0) {
            const index = this.getIndex();

            // When array is long enough, show first page before ellipses.
            if (this.activePage >= this.arrayLengthBeforeAndAfterEllipses && this.pageCount > this.maxVisiblePages) {
                pageArray.push(0);
                pageArray.push(undefined);
            }

            for (let i = index; i <= this.pageCount - 1; i++) {
                // Array can have up to seven elements.
                if (pageArray.length <= this.arrayLengthBeforeAndAfterEllipses || i >= this.pageCount - 2) {
                    pageArray.push(i);
                } else {
                    pageArray.push(undefined);
                    pageArray.push(this.pageCount - 1);
                    return pageArray;
                }
            }
        }

        return pageArray;
    }

    nextPage(pageNumber: number): void {
        if (pageNumber > this.pageCount) {
            return;
        }

        this.pageChange.emit(pageNumber);
    }

    previousPage(pageNumber: number): void {
        if (pageNumber < 0) {
            return;
        }

        this.pageChange.emit(pageNumber);
    }

    onPageClick(pageNumber: number): void {
        this.pageChange.emit(pageNumber);
    }
}

