import { ChangeDetectionStrategy, Component, ElementRef, Input } from '@angular/core';

export interface ProgressBarState {
    icon: string;
    label: string;
    color?: 'dd-success' | 'dd-accent';
}

@Component({
    selector: 'dd-progress-bar',
    templateUrl: './progress-bar.component.html',
    styleUrls: ['./progress-bar.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class ProgressBarComponent {
    constructor(private el: ElementRef) {}
    /** Value of current progress in relation to max progress. */
    @Input() progress: number = 0;
    /** Maximum progress. */
    @Input() max: number = 100;

    @Input() states?: ProgressBarState[];

    @Input() currentStateIndex?: number;

    @Input() vertical: boolean = false;

    get progressPercentage(): number {
        if (this.states && this.states.length > 0 && this.currentStateIndex !== undefined) {
            if (this.currentStateIndex >= this.states.length - 1) {
                return 100;
            }

            const progressBarLength = this.vertical ? this.el.nativeElement.getBoundingClientRect().height : this.el.nativeElement.getBoundingClientRect().width;

            const padding = 8 / progressBarLength;

            if (this.currentStateIndex === 0) {
                return (16 / progressBarLength + padding) * 100;
            }

            const stateGap = progressBarLength / (this.states.length - 1);

            const retval = ((this.currentStateIndex * stateGap) / progressBarLength + padding) * 100;

            return Math.min(retval, 100);
        }

        // Also handles 0 value
        if (!this.progress) {
            // Always show some progress (numbers are from Figma)
            return (16 / 1083) * 100;
        }

        // Can progress over max.
        if (this.progress > this.max) {
            return 100;
        }

        return (this.progress / this.max) * 100;
    }

    get labelSpace(): number {
        const progressBarLength = this.vertical ? this.el.nativeElement.getBoundingClientRect().height : this.el.nativeElement.getBoundingClientRect().width;
        if (this.states?.length) {
            const padding = progressBarLength > 600 ? 16 : 8;
            return (progressBarLength / (this.states?.length - 1)) * (2 / 3) - padding;
        }
        return 0;
    }

    get verticalBarMinHeight(): number {
        if (this.states?.length) {
            return this.states?.length * 80;
        }
        return 200;
    }

    get statesReversed(): ProgressBarState[] {
        return this.states?.slice().reverse() ?? [];
    }

    get barColor(): string {
        if (this.states && this.states.length > 0 && this.currentStateIndex !== undefined) {
            if (this.currentStateIndex < this.states.length && this.currentStateIndex >= 0 && !!this.states[this.currentStateIndex]?.color) {
                return this.states[this.currentStateIndex].color ?? '';
            }

            if (this.currentStateIndex + 1 >= this.states.length) {
                return 'dd-success';
            }
            return 'dd-accent';
        }

        if (this.progress >= this.max) {
            return 'dd-success';
        }
        return 'dd-accent';
    }
}

