import { ChangeDetectionStrategy, Component, ElementRef, EventEmitter, Input, OnDestroy, OnInit, Output, ViewChild } from '@angular/core';
import { FormControl } from '@angular/forms';
import { Subject } from 'rxjs';
import { debounceTime, takeUntil } from 'rxjs/operators';

@Component({
    selector: 'dd-search-input',
    templateUrl: './search-input.component.html',
    styleUrls: ['./search-input.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class SearchInputComponent implements OnInit, OnDestroy {
    @ViewChild('searchInputElement') searchInputElement?: ElementRef<HTMLInputElement>;

    @Input() icon: string = 'dd-icon-search';
    @Input() debounceTime: number = 500;
    @Input() placeHolder?: string = '';
    @Input() autoFocus: boolean = false;
    @Input() currentValue?: string = '';
    @Output() inputValueChanged: EventEmitter<string> = new EventEmitter();
    @Output() buttonClick: EventEmitter<string> = new EventEmitter();

    readonly searchInput: FormControl<string | null | undefined> = new FormControl('');

    /**
     * Unsubscribe subject for handling the subscriptions.
     */
    private readonly _unsubscribeAllSubject: Subject<void> = new Subject();

    ngOnInit(): void {
        if (this.currentValue) {
            this.searchInput.setValue(this.currentValue);
        }

        this.searchInput?.valueChanges.pipe(debounceTime(this.debounceTime), takeUntil(this._unsubscribeAllSubject)).subscribe((inputValue?: string | null) => {
            if (typeof inputValue === 'string') {
                this.inputValueChanged.emit(inputValue);
            }
        });
    }

    /** Completes the exposed subject. */
    ngOnDestroy(): void {
        this._unsubscribeAllSubject.next();
        this._unsubscribeAllSubject.complete();
    }
}

