import { CommonModule } from '@angular/common';
import { AfterViewInit, ChangeDetectionStrategy, Component, ElementRef, EventEmitter, HostListener, Input, OnDestroy, Output, Renderer2, ViewChild } from '@angular/core';
import { FormControl, ReactiveFormsModule } from '@angular/forms';
import { IconModule } from '@digitaldelightsoy/design-library/src/icon';
import { keyboardKeys } from '@digitaldelightsoy/design-library/src/services';
import { Subject, shareReplay } from 'rxjs';
import { debounceTime, filter, takeUntil } from 'rxjs/operators';

/**
 * This component acts as a search input field that can be used in either light or dark mode.
 *
 * TODO: This does not support changing size dynamically, also should this be removed from the library?
 */
@Component({
    standalone: true,
    imports: [
        CommonModule, //
        IconModule,
        ReactiveFormsModule,
    ],
    selector: 'dd-search-input-v2',
    templateUrl: './search-input-v2.component.html',
    styleUrls: ['./search-input-v2.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class SearchInputV2Component implements AfterViewInit, OnDestroy {
    constructor(private renderer: Renderer2) {}

    @Input() initialValue?: string;
    @Input() placeHolder?: string;
    @Input() autoFocus: boolean = false;
    @Input() disabled: boolean = false;
    @Input() variant: 'light' | 'dark' = 'light';

    @Output() inputValueChanged: EventEmitter<string> = new EventEmitter();
    /** Emits search icon click */
    @Output() buttonClick: EventEmitter<void> = new EventEmitter();

    @ViewChild('searchInputElement') searchInputElement?: ElementRef<HTMLElement>;
    @ViewChild('searchInputContainer') searchInputContainer?: ElementRef<HTMLElement>;

    readonly searchInput: FormControl<string | null | undefined> = new FormControl('');
    private readonly debounceTime: number = 500;

    /**
     * Unsubscribe subject for handling the subscriptions.
     */
    private readonly _unsubscribeAllSubject: Subject<void> = new Subject();

    ngAfterViewInit(): void {
        if (this.initialValue) {
            this.searchInput.setValue(this.initialValue);
            this.renderer.addClass(this.searchInputContainer?.nativeElement, 'has-value');
        }

        const valueChanges$ = this.searchInput.valueChanges.pipe(shareReplay(1));

        valueChanges$
            .pipe(
                debounceTime(this.debounceTime),
                filter(() => !this.disabled),
                takeUntil(this._unsubscribeAllSubject)
            )
            .subscribe((inputValue?: string | null) => {
                if (typeof inputValue === 'string') {
                    this.inputValueChanged.emit(inputValue);
                }
            });

        valueChanges$.pipe(takeUntil(this._unsubscribeAllSubject)).subscribe((inputValue?: string | null) => {
            if (inputValue && !this.disabled) {
                this.renderer.addClass(this.searchInputContainer?.nativeElement, 'has-value');
            } else {
                this.renderer.removeClass(this.searchInputContainer?.nativeElement, 'has-value');
            }
        });

        if (this.autoFocus) {
            setTimeout(() => {
                this.searchInputElement?.nativeElement.focus();
            }, 1);
        }
    }

    ngOnDestroy(): void {
        this._unsubscribeAllSubject.next();
        this._unsubscribeAllSubject.complete();
    }

    // This is needed for enter key to fire button click on mobile as well.
    @HostListener('document:keydown', ['$event'])
    handleKeyboardEvent(event: KeyboardEvent): void {
        if (event.key === keyboardKeys.enter && event.target === this.searchInputElement?.nativeElement) {
            this.onButtonClick();
        }
    }

    onButtonClick(): void {
        if (this.disabled) {
            return;
        }

        this.buttonClick.emit();
    }
}

