                border-bottom-color: themify.dd-color('primary-300');

                input {
                    color: themify.dd-color-contrast('primary-500');
                }

                .search-label {
                    color: themify.dd-color('primary-300');
                }

                dd-icon {
                    color: themify.dd-color('primary-300');
                }

                input:disabled,
                dd-icon.disabled {
                    color: themify.dd-color('primary-400') !important;
                }
            }
        }

        &:focus-within,
        &.has-value {
            dd-icon {
                @include themify.dd-themify() {
                    color: themify.dd-color('accent');
                }
            }
        }

        input {
            position: absolute;
            top: 12px;

            background-color: transparent;
            padding: 12px 35px 12px 12px;

            border: none;
        }

        .search-label {
            pointer-events: none;
            position: absolute;
            top: 16px;
            left: 10px;
            font-size: 14px;
            transition: top 0.2s ease-in, font-size 0.2s ease-in;
        }

        .search-input-icon {
            position: absolute;
            right: 1px;
            width: 35px;
            transition: color 0.2s ease-in-out;

            // Center icon
            display: flex;
            justify-content: center;
            align-items: center;
        }
    }

    .search-input-container:focus-within {
        @include themify.dd-themify() {
            border-bottom-color: themify.dd-color('accent');
        }
    }

    .search-input-container:hover,
    .search-input-container:focus-within,
    .search-input-container.has-value {
        &.light {
            @include themify.dd-themify() {
                background-color: themify.dd-color('primary-200');
            }
        }

        &.dark {
            @include themify.dd-themify() {
                background-color: themify.dd-color('primary-600');
            }
        }
    }

    .search-input-container.has-value .search-label,
    input:focus ~ .search-label {
        top: -1px;
        font-size: 11px;
    }

    .search-input-container.has-value.light .search-label,
    .light input:focus ~ .search-label {
        @include themify.dd-themify() {
            color: themify.dd-color('primary-500');
        }
    }

    .search-input-container.has-value.dark .search-label,
    .dark input:focus ~ .search-label {
        @include themify.dd-themify() {
            color: themify.dd-color('primary-300');
        }
    }
}

