import { Component, EventEmitter, Inject, Output } from '@angular/core';
import { OVERLAY_DATA } from '@digitaldelightsoy/design-library/src/overlay';
import { SelectItem, SelectListModel } from '../../select-models';

export type SelectMenuOutput = [any, (string | number)[]];

@Component({
    selector: 'dd-select-menu',
    templateUrl: './select-menu.component.html',
    styleUrls: ['./select-menu.component.scss'],
})
export class SelectMenuComponent {
    constructor(@Inject(OVERLAY_DATA) public data: SelectListModel) {
        this.selectedItems = data.selected ?? [];
    }

    /**
     * Emits the selected element and the list of all selections as output.
     */
    @Output() itemClick: EventEmitter<SelectMenuOutput> = new EventEmitter();

    selectFilter: string = '';

    readonly selectedItems: (string | number)[];

    onSearchTermChange(searchTerm: string): void {
        this.selectFilter = searchTerm;
    }

    itemSelected(item: SelectItem<any>): void {
        const selectId = item.id;

        const index = this.selectedItems.indexOf(selectId);
        if (index > -1) {
            this.selectedItems.splice(index, 1);
        } else {
            this.selectedItems.push(selectId);
        }

        // Emits the clicked event and the list of all selected elements.
        this.itemClick.emit([item, this.selectedItems]);
    }

    isSelected(item: SelectItem<any>): boolean {
        return this.selectedItems.some((x) => x === item.id);
    }
}

