import { ChangeDetectionStrategy, Component, EventEmitter, Input, Output } from '@angular/core';

export interface DdStep {
    /**
     * Icon for the step.
     */
    icon: string;

    /**
     * Title for the step.
     */
    title: string;

    /**
     * Router link url for the step.
     * If url is provided then the elements renders elements as div instead of a tag.
     */
    url?: string;
}

@Component({
    selector: 'dd-stepper',
    templateUrl: './stepper.component.html',
    styleUrls: ['./stepper.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class StepperComponent {
    /**
     * Input steps, does not render anything if steps are not defined.
     */
    @Input() steps?: DdStep[];

    /**
     * Index of the selected step.
     * Steps are only selectable backwards.
     *
     * Selected index is needed so we can identify backwards which steps are active.
     * RouterLinkActive is not sufficient for this as we don't know previous routes that are active.
     */
    @Input() selectedIndex: number = 0;

    /**
     * Outputs the index of step that was clicked.
     */
    @Output() stepClick: EventEmitter<number> = new EventEmitter();

    /** Helper for reducing the code in the template. */
    getButtonClasses(i: number): { [klass: string]: any } {
        return {
            'border-left border-radius-6-left': i === 0,
            'dd-accent dd-accent-darkest-border border-top border-bottom cursor-pointer dd-accent-darker-hover dd-accent-darkest-active': i <= this.selectedIndex,
            'border-radius-6-right': this.steps && i === this.steps.length - 1,
            bold: i === this.selectedIndex,
            'cursor-not-allowed': i > this.selectedIndex,
            'border-right': i !== this.selectedIndex,
            'move-under-arrow': this.selectedIndex + 1 === i,
        };
    }
}

