import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
    selector: 'dd-tab',
    templateUrl: './tab.component.html',
    styleUrls: ['./tab.component.scss'],
})
export class TabComponent {
    /** The title of the tab. */
    @Input() title: string = '';

    /** Optional icon for the tab. dd-icon. */
    @Input() icon?: string = '';

    /** Used to determine the selected tab. Can be set as true to set initially selected one. */
    @Input() active: boolean = false;

    /** Rendered with the tab to display the number of any notifications. */
    @Input() notifications?: number;

    @Output() tabActivated: EventEmitter<void> = new EventEmitter<void>();
}

import { AfterViewInit, ChangeDetectionStrategy, ChangeDetectorRef, Component, ElementRef, Input, OnDestroy, QueryList, ViewChild, ViewChildren } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { NavigationItem } from '@digitaldelightsoy/design-library/src/navigation';
import { OverlayOpenModel, OverlayService } from '@digitaldelightsoy/design-library/src/overlay';
import { SelectItem, SelectListModel, SelectMenuComponent, SelectMenuOutput } from '@digitaldelightsoy/design-library/src/select';
import { Subject, debounceTime, delay, fromEvent, take, takeUntil } from 'rxjs';

@Component({
    selector: 'dd-tab-navigation',
    templateUrl: './tab-navigation.component.html',
    styleUrls: ['./tab-navigation.component.scss'],
    changeDetection: ChangeDetectionStrategy.OnPush,
})
export class TabNavigationComponent implements AfterViewInit, OnDestroy {
    constructor(
        private overlayService: OverlayService, //
        private router: Router,
        private cdr: ChangeDetectorRef,
        private route: ActivatedRoute
    ) {
        fromEvent(window, 'resize')
            .pipe(debounceTime(250), takeUntil(this._unsubscribeAllSubject))
            .subscribe(() => {
                this.closeMenu();
                this.checkOverflow();
            });
    }

    @Input() tabNavigation?: NavigationItem[];

    @Input() previousNavigation?: NavigationItem;

    /**
     * Header string to show active edit view.
     * This will be shown instead of the tab navigation.
     * previousNavigation should always be defined together with this.
     */
    @Input() editActiveHeader?: string;

    @Input() closeText?: string;

    @ViewChild('tabs') tabContainer?: ElementRef;
    @ViewChildren('navigationTab') tabsList?: QueryList<ElementRef>;

    isOverflowing?: boolean;

    tabNavigationSelectItems?: SelectItem<NavigationItem>[];

    overlayModel?: OverlayOpenModel<SelectMenuComponent>;

    private readonly _unsubscribeAllSubject: Subject<undefined> = new Subject();

    ngOnDestroy(): void {
        this._unsubscribeAllSubject.next(undefined);
        this._unsubscribeAllSubject.complete();
    }

    ngAfterViewInit(): void {
        if (!this.editActiveHeader) {
            if (this.tabNavigation) {
                this.constructCollapsedItems(this.tabNavigation);
            }

            this.checkOverflow();
        }
    }

    onAnchorClick(element: HTMLDivElement): void {
        // Close existing overlay
        if (!!this.overlayModel) {
            this.closeMenu();
            return;
        }

        if (!this.tabNavigationSelectItems) {
            return;
        }

        const widthOfMenuelement: number = element.offsetWidth;

        // Filter out active tab. Compare agains The active route path, without the starting slash.
        const items = this.tabNavigationSelectItems.filter((item) => item.value?.url?.replace('/', '') !== this.route.routeConfig?.path);

        const data: SelectListModel = {
            items,
            searchEnabled: false,
            variant: 'basic',
        };

        const config = this.overlayService.getConnectedToConfig<SelectListModel>(element, '.dd-select-menu', data);

        if (config.overlayConfig) {
            config.overlayConfig.minWidth = widthOfMenuelement;
            config.closeOnOutsidePointerEvents = true;
        }

        this.overlayModel = this.overlayService.open(SelectMenuComponent, config);

        this.overlayModel.component.itemClick
            .pipe(
                take(1), //
                takeUntil(this._unsubscribeAllSubject)
            )
            .subscribe((selection: SelectMenuOutput) => {
                this.router.navigate([selection[0].value.url], { relativeTo: this.route });
                this.closeMenu();
            });

        this.overlayModel.overlayRef
            .detachments()
            .pipe(
                // Add delay so when clicking anchor (that is outside event) the anchor click happens first and doesnt open a new overlay.
                delay(1),
                take(1),
                takeUntil(this._unsubscribeAllSubject)
            )
            .subscribe(() => {
                this.closeMenu();
            });
    }

    /** Helper function to get rid of things. */
    private closeMenu(): void {
        this.overlayModel?.close();
        delete this.overlayModel;
        this.cdr.detectChanges();
    }

    private checkOverflow(): void {
        const el: HTMLElement = this.tabContainer?.nativeElement;
        if (!el) {
            return;
        }

        // Change all tabs to visible so we can check if they fit in the container.
        this.tabsList?.forEach((x) => {
            if (x.nativeElement.style.display !== 'block') {
                x.nativeElement.style.display = 'block';
            }
        });

        // Calculate if there was a scroll bar activated.
        const hasHorizontalScrollbar: boolean = el.scrollWidth > el.clientWidth;

        // If scroll bar was found, hide all tabs except the active.
        if (hasHorizontalScrollbar) {
            this.tabsList?.forEach((x) => {
                if (x.nativeElement.classList.contains('active')) {
                    x.nativeElement.style.display = 'block';
                } else {
                    x.nativeElement.style.display = 'none';
                }
            });
        }

        this.isOverflowing = hasHorizontalScrollbar;
        this.cdr.detectChanges();
    }

    private constructCollapsedItems(tabs: NavigationItem[]): void {
        this.tabNavigationSelectItems = tabs.map((item) => ({
            id: item.id,
            text: item.title,
            value: item,
        }));
    }
}

