import { AfterContentInit, Component, ContentChildren, Input, QueryList } from '@angular/core';
import { TabComponent } from '../tab/tab.component';

@Component({
    selector: 'dd-tabs',
    templateUrl: './tabs.component.html',
    styleUrls: ['./tabs.component.scss'],
})
export class TabsComponent implements AfterContentInit {
    @ContentChildren(TabComponent) tabs!: QueryList<TabComponent>;

    /** Components background can be set to one of the two variants, light or dark. Light background is default.*/
    @Input() backgroundVariant: 'light' | 'dark' = 'light';

    /** Whether there should be a container around the tab content. If not selected only a bottom border is shown.*/
    @Input() container?: boolean = false;

    ngAfterContentInit(): void {
        // Check if there are already active tabs set.
        const activeTabs = this.tabs.filter((tab) => tab.active);

        // If there are no active tab set, activate the first one.
        if (activeTabs.length === 0) {
            this.selectTab(this.tabs.first);
        }
    }

    /** Sets only one tab as active and displays its content. */
    selectTab(selectedTab: TabComponent): void {
        // Deactivate all tabs.
        this.tabs.toArray().forEach((tab) => (tab.active = false));

        if (selectedTab) {
            // Activate user selected tab.
            selectedTab.active = true;
            selectedTab.tabActivated.emit();
        }
    }
}

