import { Component, EventEmitter, Input, Output } from '@angular/core';

export type TextButtonColor = 'primary' | 'secondary';
export type TextButtonSize = 'large' | 'normal' | 'mini';

@Component({
    selector: 'dd-text-button',
    templateUrl: './text-button.component.html',
    styleUrls: ['./text-button.component.scss'],
})
export class TextButtonComponent {
    /**
     * Text for the button.
     */
    @Input() text?: string;

    /**
     * Makes the button disabled, button does not output anything when it's disabled.
     */
    @Input() disabled: boolean = false;

    /**
     * Defines the color of the button.
     */
    @Input() color: TextButtonColor = 'secondary';

    /**
     * Defines the size of the button.
     */
    @Input() size: TextButtonSize = 'normal';

    /**
     * Adds aria label to the button.
     */
    @Input() ariaLabel?: string;

    /**
     * Adds link support to button click.
     */
    @Input() url?: string | string[];

    /**
     * Allows url to be opened in new tab.
     */
    @Input() target?: '_blank';

    /**
     * Button type.
     * Submit should be used inside forms.
     */
    @Input() buttonType?: 'button' | 'submit' = 'button';

    /**
     * Outputs the click event from the button.
     */
    @Output() buttonClick: EventEmitter<void> = new EventEmitter<void>();

    /**
     * Emits click event for the button if it's not disabled.
     */
    onButtonClick(): void {
        if (this.disabled) {
            return;
        }

        this.buttonClick.emit();
    }

    /**
     * ngClass selector for the button.
     */
    get buttonClasses(): { [klass: string]: boolean } {
        return {
            strong: true,
            large: this.size === 'large',
            mini: this.size === 'mini',
            'btn-disabled': this.disabled === true,
            'dd-accent-fg dd-accent-darkest-fg-hover': this.color === 'secondary',
            'dd-primary-500-fg-hover': this.color === 'primary',
        };
    }
}

